import { createReducer } from "@reduxjs/toolkit";
import { productCategory } from "../actions/productCategory";



const initialState = {
catProducts: []    
}


export default createReducer(initialState, {
[productCategory.pending]: (state, action) => {

},

[productCategory.fulfilled]: (state, action) => {
state.catProducts = action.payload
},

[productCategory.rejected]: (state, action) => {

},
})
