import { createAsyncThunk } from "@reduxjs/toolkit";
import Api from "../../Api";



export const productCategory = createAsyncThunk('actions/productCategort/productCategory', async(id) => {
    const {data} = await Api.productCategory(id)
    return data
})


export const categoryList = createAsyncThunk('actions/productCategory/categoryList', async() => {
   const {data} = await Api.categoryList()
   return data 
})

export const productChild = createAsyncThunk('action/productCategort')
