import React, { useCallback, useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import MenuIcon from "@mui/icons-material/Menu";
import { Link } from 'react-router-dom';
import { categoryList } from '../store/actions/productCategory';


function Menu() {
    const [isOpen, setIsOpen] = useState(false);
    const catList = useSelector((state) => state.category.catList);
    const category = useSelector((state) => state.category.catProducts);
    const dispatch = useDispatch();



    useEffect(() => {
        dispatch(categoryList());
    }, []);


    const toggle = useCallback(() => {
        setIsOpen(!isOpen);
    }, [isOpen]);

    const handleClose = useCallback(() => {
        setIsOpen(false);
    }, []);

    return (
        <div>
            <div className='navMenu'>
                <MenuIcon style={{ fontSize: 50, color: 'red', marginTop: -10 }} onClick={toggle} />


            </div>


            {isOpen ? 
            <div>
                <nav className="nav">
                    <ul className="nav__block">
                        <li className="nav__list">
                            <Link to="#" className="nav__link">link-1</Link>
                        </li>
                        <li className="nav__list">
                            <Link to="#" className="nav__link">link-2</Link>
                        </li>
                        <li className="nav__list">
                            <Link to="#" className="nav__link">link-3</Link>
                        </li>
                        <li className="nav__list">
                            <Link to="#" className="nav__link">link-4</Link>
                        </li>
                        <li className="nav__list">
                            <Link to="#" className="nav__link">link-5</Link>
                        </li>
                    </ul>
                    <button className='closeButtonStyle' onClick={handleClose}>X</button>
                </nav>



            </div>
                  :
        null
        }

        </div>
    )

}


export default Menu;
