import React, { useCallback, useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import Header from '../components/Header'
import Footer from '../components/Footer'
import { getProducts } from '../store/actions/products'
import { searchProducts } from '../store/actions/products'
import { useLocation, useNavigate, useParams, useSearchParams } from "react-router-dom";
import { basketCreate, basketList } from '../store/actions/basket'
import { likeProduct, likeList } from '../store/actions/likeProducts'
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';
import FavoriteIcon from '@mui/icons-material/Favorite';
import qs from 'query-string'
import { toast } from 'react-toastify';
import Toast from '../components/Toast'


export default function Products() {


  const products = useSelector((state) => state.products.productSearch);
  const dispatch = useDispatch();
  const [searchParams, setSearchParams] = useSearchParams();
  const navigate = useNavigate();
  const userData = JSON.parse(localStorage.getItem("userData"));
  const basket = useSelector((state) => state.basket.basket)
  const likes = useSelector((state) => state.likes.likes)
  const location = useLocation()
  const query = qs.parse(location.search)
  const [open, setOpen] = useState(false)
  const [from, setFrom] = useState(0)
const [to, setTo] = useState(0)




  useEffect(() => {
    const search = searchParams.get("q");
    (async() => {
     await dispatch(searchProducts(search)); 
    })()
  }, [searchProducts]);


  const singleItem = useCallback((item) => {
    navigate(`/home/${item.id}`);
  }, [])

  const handleLike = useCallback(async (el) => {

    let error = false
    const likeId = likes.map((id) => (id.product_id))

    if (likeId.find((id) => id === el.id)) {
      error = true
      toast.error('Item already in your wishlist!', {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
    }


    if (!userData) {
      toast.error('Please login to be add item in your wishlist!', {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
      return
    }

    else if (error) {
      return
    }


    const data = await dispatch(likeProduct({

      product_id: el.id,
      user_id: userData.id
    }))
    if (data.payload) {
      toast(`${el.title} added in your wishlist`)
    }

    await dispatch(likeList())

  }, [userData])


  const handleClick = useCallback(async (el) => {


    let error = false
    const prodId = basket.map((id) => (id.product_id))


    if (prodId.find((id) => id === el.id)) {
      error = true
      toast.error('Item already in cart!', {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
    }

    if (!userData) {
      toast.error('Please login to be add card!', {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
      return
    }

    else if (error) {
      return
    }


    const data = await dispatch(basketCreate({
      user_id: userData.id,
      product_id: el.id,
      quantity: el.quantity,
      price: el.price
    }))

    if (data.payload) {
      toast.success(`${el.title} added in your basket`, {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
    }




    await dispatch(basketList())
  }, [userData])


  /*
const handlePrice = useCallback(async() => {

const data = products.filter((p) => (
  p.price >= from && p.price <= to
))

console.log(data)


 { <div>
              <button onClick={() => setOpen(!open)}>Price</button>
              {open ? 
              <div>
            <input value={from} onChange={(ev) => setFrom(ev.target.value)} type = 'number' placeholder='From' />
            <input value={to} onChange={(ev) => setTo(ev.target.value)} type = 'number' placeholder='To'/> 
            <button onClick={() => handlePrice()} type = 'submit'>Submit</button>
            </div>
            :
            null
            }
            </div> }



}, [from, to])
*/


  return (
    <div>
      <Toast />
      <Header />
      <div className='container'>
        {products.length ? (
          <div className='productCardStyle'>

            {products.map((product) => (
              <div key={product.id}>
                <div onClick={() => singleItem(product)} className="productCardStyle">
                  <img className="card-img-top" src={product.image} alt="Card image cap" />
                  <div className="card-body">
                    <h5 className="card-title">Brand: {product.title}</h5>
                    <p className="card-text desc">Description: {product.description}</p>
                    <p className="card-text">Price: {product.price + '$'}</p>
                    <p className="card-text">Quantity: {product.quantity}</p>
                    <button onClick={(e) => handleClick(product, e.stopPropagation())}><AddShoppingCartIcon style={{ fontSize: 35 }} /></button>
                    <button onClick={(e) => handleLike(product, e.stopPropagation())}><FavoriteIcon style={{ fontSize: 35 }} /></button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )
          :
          <div>
            <h2 className='nfpd'>{`Nothing found for your search <<${query.q}>>`}</h2>

          </div>
        }
      </div>

      <Footer />

    </div>
  )
}
