import React, { useCallback, useEffect } from 'react';
import { singleProduct } from '../store/actions/products';
import { useDispatch, useSelector } from 'react-redux';
import Header from './Header';
import Footer from './Footer';
import { useParams } from 'react-router-dom';
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';
import FavoriteIcon from '@mui/icons-material/Favorite';
import { toast } from 'react-toastify';
import { basketCreate, basketList } from '../store/actions/basket';
import { likeProduct, likeList } from '../store/actions/likeProducts';
import Carousel from 'nuka-carousel';
import Toast from './Toast';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import ArrowBackIosNewIcon from '@mui/icons-material/ArrowBackIosNew';


export default function SingleProduct() {
  const { productId } = useParams();
  const product = useSelector((state) => state.products.singleProduct);
  const dispatch = useDispatch();
  const user = JSON.parse(localStorage.getItem("userData"))

console.log(product)


  useEffect(() => {
    (async () => {
      await dispatch(singleProduct(productId))
    })()
  }, [singleProduct])


  const handleClick = useCallback(async (el) => {

    if (!user) {
      toast.error('Please login to be add card!', {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
      return
    }
    const data = await dispatch(basketCreate({
      user_id: user.id,
      product_id: el.id,
      quantity: el.quantity,
      price: el.price
    }))

    if (data.payload) {
      toast.success(`${el.title} added in your basket`, {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
    }


    if (!data.payload) {
      toast.error('Item already in cart!', {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
      return
    }
    console.log(data)
    await dispatch(basketList())
  }, [user])




  const handleLike = useCallback(async (el) => {


    if (!user) {
      toast.error('Please login to be add item in your wishlist!', {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
      return
    }
    const data = await dispatch(likeProduct({

      product_id: el.id,
      user_id: user.id
    }))
    if (data.payload) {
      toast(`${el.title} added in your wishlist`)
    }

    if (!data.payload) {
      toast.error('Item already in cart!', {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
      return
    }

    await dispatch(likeList())

  }, [user])




  return (
    <>
      <Header />
      <Toast />
      {product ? 
      <div>
        {product.map((items) => (   
      <div className='cardStyle'>
        <div key={items.id} className="card">
          <img className="card-img-top" src={items.images} alt="Card image cap" />
          <div className="card-body">
            <h5 className="card-title">Brand: {items.title}</h5>
            <p className="card-text desc">Description: {items.description}</p>
            <p className="card-text">Price: {items.price + '$'}</p>
            <p className="card-text">Quantity: {items.quantity}</p>
            <button onClick={() => handleClick(items)}><AddShoppingCartIcon /></button>
            <button onClick={() => handleLike(items)}><FavoriteIcon /></button>
          </div>
        </div>
      </div>
      ))}
      </div>   
      :
      null
    }


      <Footer />
    </>
  )
}
