import React, { useCallback, useEffect, useState } from 'react';
import { singleProduct } from '../store/actions/products';
import { useDispatch, useSelector } from 'react-redux';
import Header from './Header';
import Footer from './Footer';
import { useParams } from 'react-router-dom';
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';
import FavoriteIcon from '@mui/icons-material/Favorite';
import { toast } from 'react-toastify';
import { basketCreate, basketList } from '../store/actions/basket';
import { likeProduct, likeList } from '../store/actions/likeProducts';
import Carousel from 'nuka-carousel';
import Toast from './Toast';
import { prodCategory } from '../store/actions/productCategory';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import ArrowBackIosNewIcon from '@mui/icons-material/ArrowBackIosNew';


export default function SingleProduct() {
  const { productId } = useParams();
  const product = useSelector((state) => state.products.singleProduct);
  const dispatch = useDispatch();
  const user = JSON.parse(localStorage.getItem("userData"))
  const basket = useSelector((state) => state.basket.basket)
  const likes = useSelector((state) => state.likes.likes)
const catProd = useSelector((state) => state.category.products);




  useEffect(() => {
    (async () => {
     await dispatch(singleProduct(productId))
    const data = await dispatch(prodCategory(product[0].cat_id))
    console.log(data)
    })()
  }, [singleProduct])


  const handleClick = useCallback(async (el) => {


    let error = false
    const prodId = basket.map((id) => (id.product_id))


    if (prodId.find((id) => id === el.id)) {
        error = true
        toast.error('Item already in cart!', {
            position: "top-center",
            autoClose: 1000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: "dark",
        });
    }

    if (!user) {
      toast.error('Please login to be add card!', {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
      return
    }

else if(error){
  return
}

    const data = await dispatch(basketCreate({
      user_id: user.id,
      product_id: el.id,
      quantity: el.quantity,
      price: el.price
    }))

    if (data.payload) {
      toast.success(`${el.title} added in your basket`, {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
    }

    await dispatch(basketList())
  }, [user])




  const handleLike = useCallback(async (el) => {


    let error = false
    const likeId = likes.map((id) => (id.product_id))

    if (likeId.find((id) => id === el.id)) {
        error = true
        toast.error('Item already in your wishlist!', {
            position: "top-center",
            autoClose: 1000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: "dark",
        });
    }

    if (!user) {
      toast.error('Please login to be add item in your wishlist!', {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
      return
    }

else if(error){
  return
}

    const data = await dispatch(likeProduct({

      product_id: el.id,
      user_id: user.id
    }))
    if (data.payload) {
      toast(`${el.title} added in your wishlist`)
    }

    await dispatch(likeList())

  }, [user])








  return (
    <>
      <Header />
      <Toast />
      {product.length ?
        <div>
          {product.map((item) => (
            <div key={item.id} className='singleCardStyle'>
              <div className="singleCard">
                <div className='carousel'>
                  <Carousel wrapAround animation='fade' defaultControlsConfig={{
                    nextButtonText: <ArrowForwardIosIcon />,
                    prevButtonText: <ArrowBackIosNewIcon />,
                  }}>
                    {item.images.map((i) => (
                      <img key={item.id} src={i} alt='' style={{ transform: 'scale(1)'}} />
                    ))}
                  </Carousel>
                </div>

                <div className="productBody">
                  <h5 className="productTitle">{item.title}</h5>
                  <p className="productText desc">Description {item.description}</p>
                  <p className="productPrice">Price ~~~~~~~~ {item.price + '$'}</p>
                </div>
<div className='btnAdd'>
<button onClick={() => handleClick(item)}><AddShoppingCartIcon/></button>
<button onClick={() => handleLike(item)} ><FavoriteIcon/></button>
  </div>
              </div>
            </div>
          ))}
        </div>
        :
        null}
      <Footer />
    </>
  )
}
