import React, {useCallback, useEffect, useState} from 'react'
import {useDispatch, useSelector} from 'react-redux'
import { getProducts } from '../store/actions/products';
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';
import FavoriteIcon from '@mui/icons-material/Favorite';
import 'react-toastify/dist/ReactToastify.css';
import {ToastContainer, toast} from 'react-toastify';
import Spinner from 'react-bootstrap/Spinner';
import { basketCreate } from '../store/actions/basket';
import AddToCard from '../components/AddToCard';
import { category } from '../store/actions/productCategory'
import CategoryData from './CategoryData';




export default function Wrapper() {

    const dispatch = useDispatch()
    const products = useSelector((state) => state.products.productsData)
    const singleProduct = useSelector((state) => state.products.singleProduct)
    const [loading, setLoading] = useState(true)



    console.log(singleProduct)

    useEffect(() => {

        (async () => {
            setLoading(true)
            await dispatch(getProducts())
            setLoading(false)
        })()

    }, [getProducts])


    const handleClick = useCallback(async (el) => {
        await dispatch(basketCreate({
            product_id: el.id,

        }))

    }, [])



    const singleItem = useCallback(async(el) => {
await dispatch(singleProduct(el.id))

    }, [])


    return (
        <>
        <div>
            <CategoryData/>
        </div>
            {!loading ?
                <div className='cardStyle'>

                    {products.map((item) => (
                        <div key={item.id} className="card">
                            <img onClick={() => singleItem(item)} className="card-img-top" src={item.image} alt="Card image cap"/>
                            <div className="card-body">
                                <h5 className="card-title">{item.title}</h5>
                                <p className="card-text desc">{item.description}</p>
                                <p className="card-text">{item.price + '$'}</p>
                                <p className="card-text">
                                    {item.quantity}
                                </p>
                                <div className="buttons">
                                    <button><FavoriteIcon/></button>
                                    <AddToCard onClick={() => handleClick(item)}/>
                                </div>
                            </div>
                        </div>
                    ))}
                    <ToastContainer/>
                </div>
                :
                <div className='spL'>
                    <Spinner className='spinnerLoad' animation="border" variant="dark"/>

                </div>
            }

        </>
    )
}
