import React, { useCallback, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { basketList, basketRemove } from '../store/actions/basket'
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart'
import Header from '../components/Header'
import Footer from '../components/Footer'


export default function Card() {

  const dispatch = useDispatch()
  const basket = useSelector((state) => state.basket.basket)
const token = localStorage.getItem("userToken")




  useEffect(() => {
   (async ()=>{
   await dispatch(basketList())
  })()

  }, [basketList])

  const handleDelete = useCallback(async(el) => {
await dispatch(basketRemove(el.id))
await dispatch(basketList())
  }, [])


  return (
    <div>

      <Header />
<div className='cardStyle'>

      {basket.length ?
          basket.map(b => (
            <div className='cardStyle' key={b.id}>
              <div  className="card">
                <img className="card-img-top" src={b.image} alt="Card image cap" />
                <div className="card-body">
                  <h5 className="card-title">Brand: {b.title}</h5>
                  <p className="card-text desc">Description: {b.description}</p>
                  <p className="card-text">Price: {b.price + '$'}</p>
                  <p className="card-text">Quantity: {b.quantity}</p>

                  <button onClick={() => handleDelete(b)}><AddShoppingCartIcon /> Delete Product</button>
                </div>
              </div>
            </div>
          ))
        :
        <h2>Card Is empty</h2>
      }

</div>
      <Footer />


    </div>

  )
}
