import React, { useCallback, useEffect, useState } from 'react'
import { useDispatch, } from 'react-redux'
import { basketList, basketRemove } from '../store/actions/basket'
import RemoveShoppingCartIcon from '@mui/icons-material/RemoveShoppingCart';
import Toast from '../components/Toast';
import { toast } from 'react-toastify';
import PaidIcon from '@mui/icons-material/Paid';
import { useNavigate } from 'react-router-dom';

export default function CardCount({ b }) {

  const dispatch = useDispatch()
  const [count, setCount] = useState(1);
  const navigate = useNavigate()



  useEffect(() => {
    (async () => {
      await dispatch(basketList())
    })()

  }, [basketList])

  const handleDelete = useCallback(async (el) => {

    await dispatch(basketRemove(el))
    await dispatch(basketList())

  }, [])


  const handlePlus = useCallback((el) => {

    if (count >= el.quantity) {
      toast.error("You have selected the maximum quantity of goods")
      return null
    }
    else {
      setCount(count + 1)

    }


  }, [count])

  const handleMinus = useCallback((el) => {

    if (count === 1) {
      return null
    }
    else {
      setCount(count - 1)
    }
  }, [count])


  const handleBuy = useCallback((el) => {

    console.log(el)

  }, [])


  const singleItem = useCallback((item) => {
    navigate(`/home/${item}`)

  }, [])


  return (
    <div>
      <Toast />
      <div className='cardItem' key={b.product.id}>
        <div onClick={() => singleItem(b.product_id)} className="card">
          <figure>
            <div>
            <img classame="card-img-top" src={b.product.image} alt="Card image cap" />
            <h5 className="card-title">{b.product.title}</h5>
            </div>
            <figcaption className="card-body">
              <div>
              <div className='btnCount'>
                <button onClick={(e) => {
                  e.stopPropagation()
                  handleMinus(b.quantity)
                }}>-</button>
                <span> {count} </span>
                <button onClick={(e) => {
                  e.stopPropagation()
                  handlePlus(b)
                }}>+</button>
                <div>
              </div>
              <div>
              <p className="card-text">Price: {b.product.price + '$'}</p>
              </div>
                  <button onClick={(e) => handleDelete(b.id, e.stopPropagation())}>
                    <RemoveShoppingCartIcon />
                  </button>
                  <button onClick={(e) => handleBuy(b, e.stopPropagation())}>
                    <PaidIcon />
                  </button>
                </div>
              </div>
            </figcaption>
          </figure>
        </div>
      </div>
    </div>

  )
}
