import React, { useCallback, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Header from '../components/Header';
import Footer from '../components/Footer';
import { getProducts } from '../store/actions/products';
//import { productCategory } from '../store/actions/productCategory';
import { useNavigate, useParams } from 'react-router-dom';
import { prodCategory } from '../store/actions/productCategory';



export default function Category() {

const dispatch = useDispatch();
const category = useSelector((state) => state.category.products);
const {cat_id} = useParams();
const navigate = useNavigate();

console.log(products)


useEffect(() => {



  dispatch(prodCategory(cat_id))

}, []);


const singleItem = useCallback((item) => {

    navigate(`/home/${item.id}`)

}, [])



  return (
    <div>
<Header/>


<div className='cardStyle'>


{category.map((products) => (
            <div onClick={() => singleItem(products)} key={products.id} className="card">
              <img className="card-img-top" src={products.image} alt="Card image cap" />
              <div className="card-body">
                <h5 className="card-title">Brand: {products.title}</h5>
                <p className="card-text desc">Description: {products.description}</p>
                <p className="card-text">Price: {products.price + '$'}</p>
                <p className="card-text">Quantity: {products.quantity}</p>
              </div>
            </div>

))}


</div>


<Footer/>
        </div>
  )
}
