import { useDispatch, useSelector } from 'react-redux'
import { likeList, likeProduct, removeLike } from '../store/actions/likeProducts'
import Header from '../components/Header'
import Footer from '../components/Footer'
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';
import React, { useCallback, useEffect, useState } from 'react'
import Spinner from 'react-bootstrap/Spinner';
import { toast } from 'react-toastify';
import { useNavigate } from 'react-router-dom'
import { basketCreate, basketList } from '../store/actions/basket';
import RemoveShoppingCartIcon from '@mui/icons-material/RemoveShoppingCart';
import { Link } from 'react-router-dom';
import Toast from '../components/Toast';

export default function LikeProducts() {


  const [load, setLoad] = useState(true)
  const likes = useSelector((state) => state.likes.likes)
  const dispatch = useDispatch()
  const navigate = useNavigate()
  const token = localStorage.getItem("userToken")
  const userData = JSON.parse(localStorage.getItem("userData"))
  const basket = useSelector((state) => state.basket.basket)



  useEffect(() => {
    (async () => {
      setLoad(true)
      await dispatch(likeList())
      setLoad(false)
    })()

  }, [likeList])


  const handleClick = useCallback(async (el) => {


    let error = false
    const prodId = basket.map((id) => (id.product_id))



    if (prodId.find((id) => id === el.product_id)) {
      error = true
      toast.error('Item already in cart!', {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
    }


    if (!userData) {
      toast.error('Please login to be add card!', {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
      return
    }

    else if (error) {
      return
    }


    const data = await dispatch(basketCreate({
      user_id: userData.id,
      product_id: el.product.id,
      quantity: el.product.quantity,
      price: el.product.price
    }))

    if (data.payload) {
      toast.success(`${el.product.title} added in your basket`, {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
    }

    await dispatch(basketList())
  }, [userData])


  const singleItem = useCallback((item) => {
    navigate(`/home/${item.id}`)
  }, [])

  const handleDelete = useCallback(async (el) => {
    await dispatch(removeLike(el))
    await dispatch(likeList())
  }, [])


  return (

    <>
      <Toast />
      <Header />

      {token ?
        <>
          {!load ?
            <div>
              {likes.length ?
                <div className='cardStyle'>
                  {likes.map((item) => (
                    <div key={item.product.id} className='cardItem'>
                      <div onClick={() => singleItem(item.product)} className="likeCard ">
                        <img className="card-img-top" src={item.product.image} alt="Card image cap" />
                        <div className="card-body">
                          <h5 className="card-title">{item.product.title}</h5>
                          <p className="card-text desc">{item.product.short_desc}</p>
                          <p className="card-text">{item.product.price + '$'}</p>
                          <button className='btnPr' onClick={(e) => handleClick(item, e.stopPropagation())}>
                            <AddShoppingCartIcon style={{ fontSize: 35 }} />
                          </button>
                          <button className='btnPr' onClick={(e) => handleDelete(item.id, e.stopPropagation())}>
                            <RemoveShoppingCartIcon style={{ fontSize: 35 }} />
                          </button>
                        </div>
                      </div>
                    </div>
                  ))
                  }
                </div>
                :
                <div className='container'>
                   <section className='emptyWishPanel'>
                     <h1 className='emptyWishPanelTitle'>Your wish❤list is Empty.</h1>
                      <p className='emptyWishPanelDesc'> 
                      Look at the main page to select products or find what you need in the search. </p>
                       <Link to='/' className='emptyWishPanelButton'>Home Page</Link> </section> </div>
              }
            </div>

            :
            <div className='spL'>
              <Spinner className='spinnerLoad' animation="border" variant="dark" />

            </div>
          }
        </>
        :
        <div className='container'>
           <section className='wishPanel'> 
        <h1 className='wishPanelTitle'>Wish❤️list unavailable.</h1> 
        <p className='wishPanelDesc'> Please log in to see the items on your wish list. </p>
         <Link to='/account' className='wishPanelButton'>Login</Link> 
         </section> 
         </div>
      }
      <Footer />

    </>



  )
}
