import React, { useCallback, useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Header from '../components/Header';
import Footer from '../components/Footer';
import { updateProduct } from '../store/actions/products';
import { useNavigate, useParams } from 'react-router-dom';
import { prodCategory } from '../store/actions/productCategory';
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';
import FavoriteIcon from '@mui/icons-material/Favorite';
import { toast } from 'react-toastify';
import { basketCreate, basketList } from '../store/actions/basket';
import { likeProduct, likeList } from '../store/actions/likeProducts';
import Toast from '../components/Toast';
import AdminHeader from '../components/AdminHeader'
import { delProduct } from '../store/actions/products';
import RemoveShoppingCartIcon from '@mui/icons-material/RemoveShoppingCart';
import EditIcon from '@mui/icons-material/Edit';
import Modal from 'react-modal'






export default function Category() {

  const dispatch = useDispatch();
  const products = useSelector((state) => state.category.products);
  const user = JSON.parse(localStorage.getItem("userData"))
  const { cat_id } = useParams();
  const navigate = useNavigate();
  const basket = useSelector((state) => state.basket.basket)
  const likes = useSelector((state) => state.likes.likes)
  const [pr, setPr] = useState([])
  const [show, setShow] = useState(false)
  const [title, setTitle] = useState('')
  const [desc, setDesc] = useState('')
  const [price, setPrice] = useState('')
  const [short, setShort] = useState('')
  const [qnt, setQnt] = useState('')
const [mod, setMod] = useState(true)




  useEffect(() => {

    (async () => {
      await dispatch(prodCategory(cat_id))
    })()


  }, []);


  const singleItem = useCallback((item) => {

    navigate(`/home/${item.id}`)

  }, [])


  const handleClick = useCallback(async (el) => {

    let error = false
    const prodId = basket.map((id) => (id.product_id))


    if (prodId.find((id) => id === el.id)) {
      error = true
      toast.error('Item already in cart!', {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
    }


    if (!user) {
      toast.error('Please login to be add card!', {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
      return
    }

    else if (error) {
      return
    }


    const data = await dispatch(basketCreate({
      user_id: user.id,
      product_id: el.id,
      quantity: el.quantity,
      price: el.price
    }))

    if (data.payload) {
      toast.success(`${el.title} added in your basket`, {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
    }

    await dispatch(basketList())
  }, [user])


  const handleLike = useCallback(async (el) => {


    let error = false
    const likeId = likes.map((id) => (id.product_id))

    if (likeId.find((id) => id === el.id)) {
      error = true
      toast.error('Item already in your wishlist!', {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
    }

    if (!user) {
      toast.error('Please login to be add item in your wishlist!', {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
      return
    }

    else if (error) {
      return
    }


    const data = await dispatch(likeProduct({

      product_id: el.id,
      user_id: user.id
    }))
    if (data.payload) {
      toast(`${el.title} added in your wishlist`)
    }

    await dispatch(likeList())

  }, [user])


  const handleDelete = useCallback(async (pr) => {


    const data = await dispatch(delProduct(pr))
    if (data.payload) {
      toast('Product Succesfully deleted')
    }
    await dispatch(prodCategory(cat_id))

  }, [])


  const handleEdit = useCallback((p) => {

    setPr([p])

  }, [pr])


const handleSubmit = useCallback(async(ev) => {

  ev.preventDefault()
const product = pr[0]

console.log(product)



const data = await dispatch(updateProduct(product.id, {
  cat_id: product.cat_id,
  description: desc,
  id: product.id,
  image: product.image,
  images: product.images,
  price: price,
  quantity: qnt,
  short_desc: short,
  title: title,
}))


setShow(!show)

}, [show, title, desc, price, qnt, short, pr])



  return (
    <div>
      {user && user.role === 2 ? <AdminHeader /> : <Header />}
      <Toast />
      {products.length ?
        <div className='cardStyle'>

          {products.map((product) => (
            <div key={product.id} className='cardItem'>
              <div onClick={() => singleItem(product)} className="card ">
                <img className="card-img-top" src={product.image} alt="Card image cap" />
                <div className="card-body">
                  <h5 className="card-title">{product.title}</h5>
                  <p className="card-text desc">{product.short_desc}</p>
                  <p className="card-text">{product.price + '$'}</p>
                  {user && user.role === 2 ?
                    <div onClick={(e) => e.stopPropagation()}>
                      <button onClick={() => handleDelete(product.id)}>
                        Delete Product <RemoveShoppingCartIcon style={{ fontSize: 30 }} />
                      </button>
                      <button onClick={() => handleEdit(product, setShow(!show))}>
                        Edit Product <EditIcon style={{ fontSize: 30 }} />
                      </button>
                       {show ? 
                       <Modal ariaHideApp={false} isOpen={mod}>
                        <form onSubmit={(ev) => handleSubmit(ev)}>
<input value={title} onChange={(ev) => setTitle(ev.target.value)} type = 'text' placeholder='Title'/>
<input value={desc} onChange={(ev) => setDesc(ev.target.value)} type = 'text' placeholder='Description'/>
<input value={short} onChange={(ev) => setShort(ev.target.value)} type = 'text' placeholder='Short Description'/>
<input value={qnt} onChange={(ev) => setQnt(ev.target.value)} type = 'number' placeholder='Quantity'/>
<input value={price} onChange={(ev) => setPrice(ev.target.value)} type = 'number' placeholder='Price'/>
<button type = 'submit'>Add</button>
<button onClick={() => setMod()}>Close Modal</button>
</form>
                       </Modal>
                      :
                      null
                      }
                      <div>
                      </div>
                    </div>
                    : <div onClick={(e) => e.stopPropagation()}>
                      <button onClick={() => handleClick(product)}>
                        <AddShoppingCartIcon style={{ fontSize: 35 }} />
                      </button>
                      <button onClick={() => handleLike(product)}>
                        <FavoriteIcon style={{ fontSize: 35 }} />
                      </button>
                    </div>}
                </div>
              </div>
            </div>
          ))}
        </div>
        : <h2>Products in this category Empty</h2>}
      {user && user.role === 2 ? null : <Footer />}
    </div>
  )
}
