import React, { useCallback, useEffect, useState } from 'react'
import { NavLink, useNavigate } from 'react-router-dom'
import MenuIcon from '@mui/icons-material/Menu';
import { categoryList, productCategory, productChild } from '../store/actions/productCategory';
import { useDispatch, useSelector } from 'react-redux';
import { getProducts } from '../store/actions/products';
import { set } from 'lodash';


export default function CategoryData() {

  const [open, setOpen] = useState(false)
  const catList = useSelector((state) => state.category.catList)
  const category = useSelector((state) => state.category.catProducts)
  const child = useSelector((state) => state.category.child)
  const [menu, setMenu] = useState(false)
  const dispatch = useDispatch()
  const navigate = useNavigate()

  console.log(child)



  useEffect(() => {
 dispatch(categoryList())
  }, [])


  const handleClick = useCallback(async(id) => {
    console.log(id)
     await dispatch(productCategory(id))
     const data = dispatch(productChild(id))
    navigate(`/category/${id}`)
    setOpen(false)
  }, [productCategory])



  return (
    <div className='navMenu'>
      <button onClick={() => setOpen(!open)}><MenuIcon style={{ fontSize: 50 }} /></button>






    </div>
  )
}
