import React, { useCallback, useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Header from '../components/Header';
import Footer from '../components/Footer';
import { useLocation, useNavigate, useParams, useSearchParams } from 'react-router-dom';
import { prodCategory } from '../store/actions/productCategory';
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';
import FavoriteIcon from '@mui/icons-material/Favorite';
import { toast } from 'react-toastify';
import { basketCreate, basketList } from '../store/actions/basket';
import { likeProduct, likeList } from '../store/actions/likeProducts';
import Toast from '../components/Toast';
import AdminHeader from '../components/AdminHeader'
import { delProduct } from '../store/actions/products';
import RemoveShoppingCartIcon from '@mui/icons-material/RemoveShoppingCart';
import ReactPaginate from 'react-paginate';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import ArrowBackIosNewIcon from '@mui/icons-material/ArrowBackIosNew';
import qs from 'query-string'




export default function Category() {

  const dispatch = useDispatch();
  const products = useSelector((state) => state.category.products);
  const user = JSON.parse(localStorage.getItem("userData"))
  const { cat_id } = useParams();
  const [searchParams, setSearchParams] = useSearchParams([])
  const navigate = useNavigate();
  const basket = useSelector((state) => state.basket.basket)
  const likes = useSelector((state) => state.likes.likes)
  const pages = useSelector((state) => state.category.page)
  const location = useLocation()
  const query = qs.parse(location.search)


  useEffect(() => {
    (async () => {
   await dispatch(prodCategory({
    cat_id: cat_id,
    page: query.page
   }))
    })()
  }, [cat_id, query.page]);


  const goToTop = () => {
    window.scrollTo({
        top: 0,
        behavior: "smooth",
    });
};


  const singleItem = useCallback((item) => {

    navigate(`/home/${item.id}`)

  }, [])


  const handleClick = useCallback(async (el) => {

    let error = false
    const prodId = basket.map((id) => (id.product_id))


    if (prodId.find((id) => id === el.id)) {
      error = true
      toast.error('Item already in cart!', {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
    }


    if (!user) {
      toast.error('Please login to be add card!', {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
      return
    }

    else if (error) {
      return
    }


    const data = await dispatch(basketCreate({
      user_id: user.id,
      product_id: el.id,
      quantity: el.quantity,
      price: el.price
    }))

    if (data.payload) {
      toast.success(`${el.title} added in your basket`, {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
    }

    await dispatch(basketList())
  }, [user])


  const handleLike = useCallback(async (el) => {


    let error = false
    const likeId = likes.map((id) => (id.product_id))

    if (likeId.find((id) => id === el.id)) {
      error = true
      toast.error('Item already in your wishlist!', {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
    }

    if (!user) {
      toast.error('Please login to be add item in your wishlist!', {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
      return
    }

    else if (error) {
      return
    }


    const data = await dispatch(likeProduct({

      product_id: el.id,
      user_id: user.id
    }))
    if (data.payload) {
      toast(`${el.title} added in your wishlist`)
    }

    await dispatch(likeList())

  }, [user])


  const handleDelete = useCallback(async (pr) => {


    const data = await dispatch(delProduct(pr))
    if (data.payload) {
      toast('Product Succesfully deleted')
    }
    await dispatch(prodCategory({
      cat_id: cat_id,
      page: query.page 
    }))
  }, [])

  const handlePage = useCallback(async(p) => {

const data = await dispatch(prodCategory({
  cat_id: cat_id,
  page: p
 }))
 if(data.payload){
  goToTop()
  navigate(`?page=${p}`)
 }

  }, [cat_id])




  return (
    <div className='category'>
      {user && user.role === 2 ? <AdminHeader /> : <Header />}
      <Toast />
      {products.length ?
        <div className='cardStyle'>

          {products.map((product) => (
            <div key={product.id} className='cardItem'>
              <div onClick={() => singleItem(product)} className="card ">
                <img className="card-img-top" src={product.image} alt="Card image cap" />
                <div className="card-body">
                  <h5 className="card-title">{product.title}</h5>
                  <p className="card-text desc">{product.short_desc}</p>
                  <p className="card-text">{product.price + '$'}</p>
                  {user && user.role === 2 ?
                   <button onClick={(e) => handleDelete(product.id, e.stopPropagation())}>
                   Delete Product <RemoveShoppingCartIcon style={{ fontSize: 30 }} />
                  </button>
                    : <div onClick={(e) => e.stopPropagation()}>
                      <button onClick={() => handleClick(product)}>
                        <AddShoppingCartIcon style={{ fontSize: 35 }} />
                      </button>
                      <button onClick={() => handleLike(product)}>
                        <FavoriteIcon style={{ fontSize: 35 }} />
                      </button>
                    </div>}
                </div>
              </div>
            </div>
          ))}
        </div>
        : <h2>Products in this category Empty</h2>}
         <div className='pages'>
            <div className='page'>
                {pages.map((p, index) =>
                    <div key={index}>
                        <ReactPaginate className='paginate'
                            activeClassName='activePage'
                            breakLabel="..."
                            nextLabel={<ArrowForwardIosIcon />}
                            onPageChange={(ev) => handlePage(ev.selected + 1)}
                            pageRangeDisplayed={5}
                            pageCount={p.pageCount}
                            previousLabel={<ArrowBackIosNewIcon />}
                            renderOnZeroPageCount={null}
                        />
                    </div>
                )}
            </div>
        </div>
      {user && user.role === 2 ? null : <Footer />}
    </div>
  )
}
