import React, { useCallback, useEffect, useState } from 'react';
import { singleProduct } from '../store/actions/products';
import { useDispatch, useSelector } from 'react-redux';
import Header from './Header';
import Footer from './Footer';
import { useNavigate, useParams } from 'react-router-dom';
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';
import FavoriteIcon from '@mui/icons-material/Favorite';
import { toast } from 'react-toastify';
import { basketCreate, basketList } from '../store/actions/basket';
import { likeProduct, likeList } from '../store/actions/likeProducts';
import Carousel from 'nuka-carousel';
import Toast from './Toast';
import { prodCategory } from '../store/actions/productCategory';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import Zoom from 'react-img-hover-zoom';
import ArrowBackIosNewIcon from '@mui/icons-material/ArrowBackIosNew';
import AdminHeader from '../components/AdminHeader'
import Update from './Update';
import StarRatings from 'react-star-ratings';



export default function SingleProduct() {
  const { productId } = useParams();
  const product = useSelector((state) => state.products.singleProduct);
  const dispatch = useDispatch();
  const user = JSON.parse(localStorage.getItem("userData"))
  const basket = useSelector((state) => state.basket.basket)
  const likes = useSelector((state) => state.likes.likes)
  const catProd = useSelector((state) => state.category.products);
  const navigate = useNavigate()



  useEffect(() => {
    (async () => {
      await dispatch(singleProduct(productId))
      await dispatch(prodCategory({
        cat_id: product.map((id) => id.cat_id)
      }))
    })()

  }, [product, singleProduct])
  /*
    useEffect(() => {
      (async () => {
       await dispatch(prodCategory({
        cat_id: product.map((id) => id.cat_id)
       }))
      })()
    }, [product])
  */


  const handleClick = useCallback(async (el) => {


    let error = false
    const prodId = basket.map((id) => (id.product_id))


    if (prodId.find((id) => id === el.id)) {
      error = true
      toast.error('Item already in cart!', {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
    }

    if (!user) {
      toast.error('Please login to be add card!', {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
      return;
    }

    else if (error && user) {
      return;
    }

    const data = await dispatch(basketCreate({
      user_id: user.id,
      product_id: el.id,
      quantity: el.quantity,
      price: el.price
    }))

    if (data.payload) {
      toast.success(`${el.title} added in your basket`, {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
    }

    await dispatch(basketList())
  }, [user])




  const handleLike = useCallback(async (el) => {


    let error = false
    const likeId = likes.map((id) => (id.product_id))

    if (likeId.find((id) => id === el.id)) {
      error = true
      toast.error('Item already in your wishlist!', {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
    }

    if (!user) {
      toast.error('Please login to be add item in your wishlist!', {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
      return
    }

    else if (error && user) {
      return
    }

    const data = await dispatch(likeProduct({

      product_id: el.id,
      user_id: user.id
    }))
    if (data.payload) {
      toast(`${el.title} added in your wishlist`)
    }

    await dispatch(likeList())

  }, [user])


  const singleItem = useCallback((id) => {
    navigate(`/home/${id}`)
    window.scrollTo({
      top: 0,
      behavior: 'smooth'
    })
  }, [])

  const handleStar = useCallback(async(st) => {

    const data = await dispatch(createRating({
        product_id: st.item.id,
        user_id: userData.id,
        rate: st.rating
    }))
console.log(data)

}, [])





  return (
    <>
      {user && user.role === 2 ? <AdminHeader /> : <Header />}

      <Toast />
      {product.length ?
        <div>
          {product.map((item) => (
            <div key={item.id} className='singleCardStyle'>
              <div className="singleCard">
                <div className='carousel'>
                  <Carousel wrapAround animation='fade' defaultControlsConfig={{
                    nextButtonText: <ArrowForwardIosIcon />,
                    prevButtonText: <ArrowBackIosNewIcon />,
                  }}>
                    {item.images.map((i) => (
                      <figure key={i.id} >
                        <Zoom
                          zoomScale={2}
                          width={450}
                          img={i}
                          height={450}
                          style={{ transform: 'scale(1)' }}
                          className='zoom'
                        />
                      </figure>
                    ))}
                  </Carousel>
                </div>

                <div className="productBody">
                  <h5 className="productTitle">{item.title}</h5>
                  <p className="productPrice">Price {item.price + '$'}</p>
                  <p className="productText desc">Description {item.description}</p>
                  <button onClick={(e) => e.stopPropagation()} className='stars'>
                    <StarRatings
                      rating={0}
                      starRatedColor="white"
                      starHoverColor='yellow'
                      starDimension='22px'
                      starSpacing='9px'
                      starEmptyColor='grey'
                      changeRating={(ev) => handleStar({ rating: ev, item: item })}
                      numberOfStars={5}
                      name='rating'
                    />
                  </button>
                </div>
                {user && user.role !== 2 ? <div className='btnAdd'>
                  <button onClick={() => handleClick(item)} className='addToCard'>
                    <AddShoppingCartIcon style={{ fontSize: 30 }} /> Add To Card
                  </button>
                  <button onClick={() => handleLike(item)} className='addToWishList'>
                    <FavoriteIcon style={{ color: 'red', fontSize: 30 }} /> Add To Wishlist
                  </button>
                </div>
                  :
                  <Update item={item} key={item.id} />
                }
              </div>
            </div>
          ))}
        </div>
        :
        null}
      <div className='simPr'>
        <h2>Similar products</h2>
      </div>
      <Carousel
        slidesToShow={4}
        renderBottomCenterControls={false}
        defaultControlsConfig={{
          nextButtonText: <ArrowForwardIosIcon />,
          prevButtonText: <ArrowBackIosNewIcon />,
        }
        }
      >

        {catProd.map((pr) => (
          <div className='cardStyleBottom'>
            <div onClick={() => singleItem(pr.id)} className='cardItemBottom'>
              <img className="card-img-top" src={pr.image} alt="Card image cap" />
              <div className="card-body">
                <p className="card-text">{pr.price + '$'}</p>
                <p className="card-text desc">{pr.description}</p>
              </div>
            </div>
          </div>
        ))}
      </Carousel>
      {user && user.role !== 2 ? <Footer /> : null}
    </>
  )
}
