import React, {useCallback, useState} from 'react';
import {Link} from "react-router-dom";
import MenuIcon from "@mui/icons-material/Menu";


function Menu() {
    const [isOpen, setIsOpen] = useState(false);

    const toggle = useCallback(() => {
        setIsOpen(!isOpen);
    }, [isOpen]);

    const handleClose = useCallback(() => {
        setIsOpen(false);
    }, []);

    return (
        <>
            <MenuIcon onClick={toggle}/>
            {isOpen ? (
                <nav className="nav">
                    <ul className="nav__block">
                        <li className="nav__list">
                            <Link to="#" className="nav__link">link-1</Link>
                        </li>
                        <li className="nav__list">
                            <Link to="#" className="nav__link">link-2</Link>
                        </li>
                        <li className="nav__list">
                            <Link to="#" className="nav__link">link-3</Link>
                        </li>
                        <li className="nav__list">
                            <Link to="#" className="nav__link">link-4</Link>
                        </li>
                        <li className="nav__list">
                            <Link to="#" className="nav__link">link-5</Link>
                        </li>
                    </ul>
                    <button className='closeButtonStyle' onClick={handleClose}>X</button>
                </nav>
            ) : null}
