import React, { useCallback, useEffect, useRef, useState } from 'react'
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import PhoneIcon from '@mui/icons-material/Phone';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import FavoriteIcon from '@mui/icons-material/Favorite';
import SearchIcon from '@mui/icons-material/Search';
import logo from '../assets/img/logo/logo.png';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { searchProducts } from '../store/actions/products';
import useTypewriter from "react-typewriter-hook";
import LogoutIcon from '@mui/icons-material/Logout';
import Menu from './Menu';

let index = 0;
const typing = [
    "Laptops...",
    "Electronics...",
    "Telephones...",
    "Clothes..."
];
export default function Header() {
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const [likeValue, setLikeValue] = useState(0);
    const [cardValue, setCardValue] = useState(0);
    const [value, setValue] = useState('');
    const [open, setOpen] = useState(false);
    const [placeholderValue, setPlaceholderValue] = useState("Search here...");
    const token = localStorage.getItem("userToken")
    const intervalRef = useRef({});
    const ref = useRef();
    const hints = useTypewriter(placeholderValue);
    const basketList = useSelector((state) => state.basket.basket)


    useEffect(() => {
        const handleOutsideClick = (event) => {
            if (!ref?.current?.contains(event.target)) {
                setOpen(false);
            }
        }
        window.addEventListener('click', handleOutsideClick);
    }, [ref]);


useEffect(() => {
(async() => {
  await dispatch(basketList())   
})

}, [basketList])


    useEffect(
        () => {
            intervalRef.current = setInterval(() => {
                // index = index + 1 > 2 ? 0 : ++index + 1;
                index = index > 2 ? 0 : ++index;
                setPlaceholderValue(typing[index]);
            }, 6000);
            return function clear() {
                clearInterval(intervalRef.current);
            };
        }, [placeholderValue]);

    const toggle = useCallback((open) => {
        setOpen(!open);
    }, []);



    const handleSubmit = useCallback(async (e) => {
        e.preventDefault()
        if (value.length) {
            await dispatch(searchProducts(value));
            const product = '/products?q=';
            navigate(`${product}${value}`);
        } else {
            alert('Value is not defined');
        }
    }, [value]);

    const handleMenu = useCallback(() => {
        setOpen(false);
    }, []);


    const handleLogout = useCallback(() => {
        localStorage.removeItem("userToken")
        localStorage.removeItem("userData")
    }, [])


    return (
        <>
            <header>
                <div id="topHeader">
                    <div className="container">
                        <div className="topHeaderLinks">
                            <ul className="headerLinks">
                                <li className='listItemStyle'>
                                    <Link to='tel:+37477053242' className='phoneNumber'>
                                        <PhoneIcon style={{ fontSize: 30, color: 'white' }} />
                                    </Link>
                                </li>

                                <li>
                                    <AccountCircleIcon
                                        ref={ref}
                                        onClick={() => toggle(open)}
                                        style={{ fontSize: 40, color: '#fff', cursor: 'pointer' }} />
                                    {open ?
                                        <div>
                                            {token ?
                                                <ul className="dropdownMenu">
                                                    <span className='triangle'></span>
                                                    <li className="menu-item">
                                                        <button onClick={() => handleLogout()} className='last'><LogoutIcon /> Log out</button>
                                                    </li>
                                                </ul>
                                                :
                                                <ul className="dropdownMenu">
                                                <span className='triangle'></span>
                                                <li className="menu-item">
                                                <Link to='/register'>Registration</Link>
                                            </li>
                                            <li className="menu-item">
                                                <Link to='/account'>Login</Link>
                                            </li>
                                            </ul>

                                            }




                                        </div>


                                        : null}
                                </li>
                            </ul>
                        </div>
                    </div>
                </div>
                <div id="header">
                    <div className="container">
                        <div className="headerLine">
                            <div className="menu">
                               <Menu/>
                            </div>
                            <div className="headerLogo">
                                <Link to="/" className="logo">
                                    <img src={logo} alt="logo" />
                                </Link>
                            </div>
                            <div className="headerSearch">
                                <form onSubmit={(event) => handleSubmit(event)}>
                                    <label>
                                        <input
                                            value={value}
                                            onChange={(ev) => setValue(ev.target.value)}
                                            type='search'
                                            className="input"
                                            placeholder={hints}
                                        />
                                    </label>
                                    <button type='submit' className="searchBtn">
                                        <SearchIcon style={{ fontSize: 30 }} />
                                    </button>
                                </form>
                            </div>
                            <div className="clearfix">
                                <div className="headerCtn">
                                    <div className='likeCorner'>
                                        <a href='/likeproducts' className='btnIcon'>
                                            <FavoriteIcon style={{ fontSize: 40, color: 'red', }} />
                                            <span className="qty liked">{likeValue}</span>
                                        </a>
                                    </div>
                                    <div className='cartCorner'>
                                        <button onClick={() => navigate('/card')} className='btnIcon'>
                                            <ShoppingCartIcon style={{ fontSize: 40, color: 'lightcyan' }} />
                                            <span className="qty cart">{cardValue}</span>
                                        </button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </header>
        </>
    )
}
