import { useDispatch, useSelector } from 'react-redux'
import { likeList, likeProduct, removeLike } from '../store/actions/likeProducts'
import Header from '../components/Header'
import Footer from '../components/Footer'
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';
import React, { useCallback, useEffect, useState } from 'react'
import Spinner from 'react-bootstrap/Spinner';
import { toast, ToastContainer } from 'react-toastify';
import { useNavigate } from 'react-router-dom'
import { basketCreate, basketList } from '../store/actions/basket';
import ThumbDownSharpIcon from '@mui/icons-material/ThumbDownSharp';


export default function LikeProducts() {


  const [load, setLoad] = useState(true)
  const likes = useSelector((state) => state.likes.likes)
  const dispatch = useDispatch()
  const navigate = useNavigate()
  const token = localStorage.getItem("userToken")
  const userId = JSON.parse(localStorage.getItem("userData"))
const likeId = useSelector((state) => state.likes.likeId)


console.log(likeId)

  console.log(likes)

  useEffect(() => {
    (async () => {
      setLoad(true)
      await dispatch(likeList())
      setLoad(false)
    })()

  }, [likeList])


  const handleClick = useCallback((el) => {
console.log(el)

  }, [])

  const singleItem = useCallback((item) => {
    navigate(`/home/${item.id}`)
  }, [])

  const handleDisLike = useCallback(async(el) => {
await dispatch(removeLike())
await dispatch(likeList())
  }, [])


  return (

    <>
      <ToastContainer
        position="top-center"
        autoClose={500}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="colored"
      />



      <Header />

      {token && userId.id ?   
      <>
      {!load ?
        <div>
          {likes.length ?
            <div className='cardStyle'>
              {likes.map((item) => (
                <div key={item.likes.id} className='productsDiv'>
                  <div onClick={() => singleItem(item)} className="card ">
                    <img className="card-img-top" src={item.likes.image} alt="Card image cap" />
                    <div className="card-body">
                      <h5 className="card-title">{item.likes.title}</h5>
                      <p className="card-text desc">{item.likes.description}</p>
                      <p className="card-text">{item.price + '$'}</p>
                      <p className="card-text">
                        {item.quantity}
                      </p>
                    </div>
                  </div>
                  <div className='btnChange'>
                  <button onClick={() => handleClick(item.id)}><AddShoppingCartIcon style={{ fontSize: 35 }} /></button>
                  <button onClick={() => handleDisLike(item)}><ThumbDownSharpIcon style={{ fontSize: 35 }} /></button>
</div>
                </div>
              ))
              }
            </div>
            :
            <h2>Likes is Empty</h2>
          }
        </div>

        :
        <div className='spL'>
          <Spinner className='spinnerLoad' animation="border" variant="dark" />

        </div>
      }
      </>
      :
      <h2>Your wishlist items is empty</h2>
}
      <Footer />

    </>



  )
}
