import React, { useCallback, useEffect, useState } from 'react';
//import ReactStars from "react-rating-stars-component";
import StarRatings from 'react-star-ratings';
import { useDispatch, useSelector } from 'react-redux'
import { getProducts } from '../store/actions/products';
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';
import FavoriteIcon from '@mui/icons-material/FavoriteBorder';
import 'react-toastify/dist/ReactToastify.css';
import Spinner from 'react-bootstrap/Spinner';
import { basketCreate, basketList } from '../store/actions/basket';
import { useNavigate } from 'react-router-dom';
import { likeList, likeProduct } from '../store/actions/likeProducts';
import { toast } from 'react-toastify';
import ArrowUpwardIcon from '@mui/icons-material/ArrowUpward';
import { createRating } from '../store/actions/rating';
import _ from 'lodash';
import Toast from './Toast';
import ReactPaginate from 'react-paginate';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import ArrowBackIosNewIcon from '@mui/icons-material/ArrowBackIosNew';
import { pageProduct } from '../store/actions/products';


export default function Wrapper() {

    const dispatch = useDispatch();
    const products = useSelector((state) => state.products.productsData);
    const navigate = useNavigate();
    const [loading, setLoading] = useState(true);
    const [show, setShow] = useState(false)
    const userData = JSON.parse(localStorage.getItem("userData"));
    const basket = useSelector((state) => state.basket.basket)
    const likes = useSelector((state) => state.likes.likes)
    const [rating, setRating] = useState(0)
    const page = useSelector((state) => state.products.pages)


    useEffect(() => {

        (async () => {
            setLoading(true)
            await dispatch(getProducts())
            setLoading(false)
        })()

    }, [getProducts]);

    useEffect(() => {
        window.addEventListener('scroll', () => {
            (window.pageYOffset > 300)
                ? setShow(true)
                : setShow(false)
        });
    }, []);

    const goToTop = () => {
        window.scrollTo({
            top: 0,
            behavior: "smooth",
        });
    };



    const handleClick = useCallback(async (el) => {

        console.log(el)



        let error = false
        const prodId = basket.map((id) => (id.product_id))



        if (prodId.find((id) => id === el.id)) {
            error = true
            toast.error('Item already in cart!', {
                position: "top-center",
                autoClose: 1000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                theme: "dark",
            });
        }


        if (!userData) {
            toast.error('Please login to be add card!', {
                position: "top-center",
                autoClose: 1000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                theme: "dark",
            });
            return
        }

        else if (error) {
            return
        }


        const data = await dispatch(basketCreate({
            user_id: userData.id,
            product_id: el.id,
            quantity: el.quantity,
            price: el.price
        }))

        if (data.payload) {
            toast.success(`${el.title} added in your basket`, {
                position: "top-center",
                autoClose: 1000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                theme: "dark",
            });
        }

        await dispatch(basketList())
    }, [userData])


    const handleLike = useCallback(async (el) => {

        let error = false
        const likeId = likes.map((id) => (id.product_id))



        if (likeId.find((id) => id === el.id)) {
            error = true
            toast.error('Item already in your wishlist!', {
                position: "top-center",
                autoClose: 1000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                theme: "dark",
            });
        }

        if (!userData) {
            toast.error('Please login to be add item in your wishlist!', {
                position: "top-center",
                autoClose: 1000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                theme: "dark",
            });
            return
        }

        else if (error && userData) {
            return
        }


        const data = await dispatch(likeProduct({
            product_id: el.id,
            user_id: userData.id
        }))
        if (data.payload) {
            toast(`${el.title} added in your wishlist`)
        }
        await dispatch(likeList())
    }, [userData])


    const singleItem = useCallback((item) => {
        navigate(`/home/${item.id}`)
    }, [])



    const handleRating = useCallback(async (element) => {
        console.log(rating)
        //console.log(rating)
        /*
        const data = await dispatch(createRating({
            product_id: element.id,
            user_id: userData.id,
            rate: rating
        }))
        console.log(data)
        */
    }, [rating])


    const handlePage = useCallback(async (p) => {
        const current = page[0].currentPage
        if (current === p) {
            return null
        }

        const data = await dispatch(getProducts(p))
        if (data.payload) {
            goToTop()
        }
    }, [page, getProducts])


    const handleActive = useCallback((e) => {

    }, [])





    return (
        <>
            <Toast />
            <div className='main'>
                <div className='container'>
                    {!loading ?
                        <div className='cardStyle'>
                            {products.map((item) => (
                                <div key={item.id} className='cardItem'>
                                    <div>
                                        <div onClick={() => singleItem(item)} className="card">
                                            <figure className='figure'>
                                                <img className="card-img-top" src={item.image} alt="Card image cap" />
                                                <figcaption className="card-body">
                                                    <h5 className="card-title">{item.title}</h5>
                                                    <p className="card-text desc">{item.short_desc}</p>
                                                    <p className="card-text">{item.price + '$'}</p>
                                                    <button onClick={(e) => handleClick(item, e.stopPropagation())}>
                                                        <AddShoppingCartIcon style={{ fontSize: 35 }} /> Add to card
                                                    </button>
                                                    <button onClick={(e) => handleRating(item, e.stopPropagation())} className='stars'>
                                                        <StarRatings
                                                            rating={0}
                                                            starRatedColor="white"
                                                            starHoverColor='yellow'
                                                            starDimension='22px'
                                                            starSpacing='9px'
                                                            starEmptyColor='grey'
                                                            changeRating={(ev) => setRating(ev)}
                                                            numberOfStars={5}
                                                            name='rating'
                                                        />
                                                    </button>
                                                </figcaption>
                                            </figure>
                                        </div>
                                        <div className='btnChange'>
                                            <button onClick={() => handleLike(item)} className='likeButton'>
                                                <FavoriteIcon style={{ fontSize: 35, color: 'red' }} />
                                            </button>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                        :
                        <div className='spL'>
                            <Spinner className='spinnerLoad' animation="border" variant="dark" />
                        </div>

                    }
                    <div>
                        {show && (
                            <div className='top' onClick={goToTop}>
                                <ArrowUpwardIcon style={{ fontSize: 30, fill: 'white' }} />
                            </div>
                        )}
                    </div>
                    <div className='pages'>
                        <div className='page'>
                            {page.map((p) =>
                                <ReactPaginate className='paginate'
                                onPageActive={(ev) => handleActive(ev)}
                                    breakLabel="..."
                                    nextLabel={<ArrowForwardIosIcon />}
                                    onPageChange={(ev) => handlePage(ev.selected + 1)}
                                    pageRangeDisplayed={5}
                                    pageCount={p.pageCount}
                                    previousLabel={<ArrowBackIosNewIcon />}
                                    renderOnZeroPageCount={null}
                                />
                            )}
                        </div>


                    </div>
                </div>
            </div>
        </>
    )
}
