import React, { useCallback, useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getProducts } from '../store/actions/action'
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';
import FavoriteIcon from '@mui/icons-material/Favorite';
import 'react-toastify/dist/ReactToastify.css';
import { ToastContainer, toast } from 'react-toastify';
import Spinner from 'react-bootstrap/Spinner';
import { basketAdd } from '../store/actions/action';
import { basketCreate } from '../store/actions/action';


export default function Wrapper() {

  const dispatch = useDispatch()
  const products = useSelector((state) => state.data)
  const [loading,setLoading] = useState(true)




  useEffect(() => {

   (async()=>{
setLoading(true)
     await dispatch(getProducts())
    setLoading(false)
  })()

  }, [getProducts])



const handleClick = useCallback(async(el) => {
const data = await dispatch(basketAdd(el.id))
console.log(data)
}, [])


 const handlePlus = useCallback((item) => {
item.quantity 

 }, [])

 const handleMinus = useCallback((item) => {


   }, [])



  return (
    <>
    {!loading ?    
      <div className='cardStyle'>
        {products.map((item) => (
          <div key={item.id} className="card">
            <img className="card-img-top" src={item.image} alt="Card image cap" />
            <div className="card-body">
              <h5 className="card-title">{item.title}</h5>
              <p className="card-text desc">{item.description}</p>
              <p className="card-text">{item.price + '$'}</p>
              <p className="card-text">

                <button onClick={() => handleMinus(item)}>-</button>
                <span>{item.quantity}</span>

                <button onClick={() => handlePlus(item)}>+</button>

                </p>

                <button><FavoriteIcon /></button>
                <button>Add to Basket <AddShoppingCartIcon/></button>
            </div>
          </div>
        ))}
        <ToastContainer/>
      </div>
      :
      <div className='spL'>
       <Spinner className='spinnerLoad' animation="border" variant="dark" />

       </div>
}

    </>
  )
}
