import { createReducer } from "@reduxjs/toolkit";
import { getProducts, searchProducts } from "../actions/products";

const initialState = {
    productData: [],   // obshi producti datan home eji mej
    productDataStatus: '', //loadingi hamar status home eji mej
    productSearch: [],
}



export default createReducer(initialState,  {
    [getProducts.pending]: (state, action) => {
        state.productDataStatus = 'Ok'
    },
    [getProducts.fulfilled]: (state, action) => {
        state.data = action.payload.products
    },
    [getProducts.rejected]: (state, action) => {
    },


    [searchProducts.pending]: (state, action) => {
        state.dataStatus = 'Ok'
    },
    [searchProducts.fulfilled]: (state, action) => {
        state.productData = action.payload.products
    },
    [searchProducts.rejected]: (state, action) => {
    }
})
