import { createReducer } from "@reduxjs/toolkit";
import { productCategory } from "../actions/productCategory";
import { categoryList } from "../actions/productCategory";


const initialState = {
catProducts: [],
catList: [],
child: []    
}


export default createReducer(initialState, {
[productCategory.pending]: (state, action) => {

},

[productCategory.fulfilled]: (state, action) => {
state.catProducts = action.payload.products
},

[productCategory.rejected]: (state, action) => {

},

[categoryList.pending]: (state, action) => {

},
[categoryList.fulfilled]: (state, action) => {
state.catList = action.payload.categories
},
[categoryList.rejected]: (state, action) => {

}
})
