import React, { useCallback, useEffect, useState } from 'react'
import MenuIcon from '@mui/icons-material/Menu';
import { useDispatch, useSelector } from 'react-redux';
import { categoryChild, categoryList } from '../store/actions/productCategory';



export default function CategoryData() {

  const [open, setOpen] = useState(false)
  const [menu, setMenu] = useState(false)
  const list = useSelector((state) => state.category.catList)
  const childs = useSelector((state) => state.category.childs) 
  const dispatch = useDispatch()

console.log(childs)


  useEffect(() => {
dispatch(categoryList())

  }, [])


  const handleClick = useCallback(async(el) => {
setMenu(!menu)
    await dispatch(categoryChild())

  }, [])







  return (
    <div className='navMenu'>
      <button onClick={() => setOpen(!open)}><MenuIcon style={{ fontSize: 50 }} /></button>

{open ? 
<>
{list.map((item) => (
  <div key={item.id}>
  <button onClick={() => handleClick(item)}>{item.title}</button>

  </div>
))}

</>
:
null
}
{menu ? 
<div>
{childs.map((ch) => (
<ul key={ch.id}>
<li>[ch]</li>

</ul>
))}

</div>
:


null
}

    </div>
  )
}
