import React, { useCallback, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Header from '../components/Header';
import Footer from '../components/Footer';
import { getProducts } from '../store/actions/products';
//import { productCategory } from '../store/actions/productCategory';
import { useNavigate, useParams } from 'react-router-dom';
import { prodCategory } from '../store/actions/productCategory';



export default function Category() {

  const dispatch = useDispatch();
  const products = useSelector((state) => state.category.products);
  const { cat_id } = useParams();
  const navigate = useNavigate();

  console.log(products)


  useEffect(() => {

    (async () => {
      await dispatch(prodCategory(cat_id))
    })()


  }, []);


  const singleItem = useCallback((item) => {

    navigate(`/home/${item.id}`)

  }, [])


  const handleClick = useCallback(() => {



  })



  return (
    <div>
      <Header />
      <div className='cardStyle'>
        {products.map((product) => (
          <div key={product.id} className='cardItem'>
            <div onClick={() => singleItem(product)} className="card ">
              <img className="card-img-top" src={product.image} alt="Card image cap" />
              <div className="card-body">
                <h5 className="card-title">{product.title}</h5>
                <p className="card-text desc">{product.description}</p>
                <p className="card-text">{product.price + '$'}</p>
              </div>
            </div>
            <div className='btnChange'>
              <button onClick={() => handleClick(product)}><AddShoppingCartIcon style={{ fontSize: 35 }} /></button>
              <button onClick={() => handleLike(product)}><FavoriteIcon style={{ fontSize: 35 }} /></button>
            </div>
          </div>
        ))}
      </div>
      <Footer />
    </div>
  )
}
