import React, { useCallback, useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import MenuIcon from "@mui/icons-material/Menu";
import { categoryList } from '../store/actions/productCategory';


function Menu() {
    const [isOpen, setIsOpen] = useState(false);
    const catList = useSelector((state) => state.category.catList);
    const category = useSelector((state) => state.category.catProducts);
    const dispatch = useDispatch();


    useEffect(() => {
        dispatch(categoryList());
    }, []);


    const handleClick = useCallback((id) => {
        console.log(id)
    }, []);


    const toggle = useCallback(() => {
        setIsOpen(!isOpen);
    }, [isOpen]);

    const handleClose = useCallback(() => {
        setIsOpen(false);
    }, []);

    return (
        <>
            <div className='navMenu'>
                <MenuIcon style={{ fontSize: 50, color: 'white', marginTop: -10 }} onClick={toggle} />
                {isOpen ? (
                    <>

                        <div className='navBtn'>
                            {catList.map((item) => (
                                <div className='menuButton' key={item.id}>
                                    <button onClick={() => handleClick(item.id)}>{item.title}</button>
                                </div>
                            ))}
                        </div>
                        <button className='closeButtonStyle' onClick={handleClose}>X</button>
                    </>
                ) : null}
            </div>
        </>
    );
}

export default Menu;
