import React, {useCallback, useState, useEffect} from 'react';
import {useDispatch, useSelector} from 'react-redux';
import MenuIcon from "@mui/icons-material/Menu";
import {Link} from 'react-router-dom';
import {categoryList} from '../store/actions/productCategory';
import {slide as Burger} from 'react-burger-menu'


function Menu() {
    const [isOpen, setIsOpen] = useState(false);
    const catList = useSelector((state) => state.category.catList);
    const category = useSelector((state) => state.category.catProducts);
    const dispatch = useDispatch();


    useEffect(() => {
        dispatch(categoryList());
    }, []);



    const toggle = useCallback(() => {
        setIsOpen(!isOpen);
    }, [isOpen]);

    // const handleClose = useCallback(() => {
    //     setIsOpen(false);
    // }, []);

    return (
        <div className='nav'>
            <MenuIcon style={{fontSize: 50, color: 'white'}} onClick={toggle}/>
            {isOpen ?
            <>
            {catList.map((items) => (
                <Burger className="bm-menu-wrap nav">
                    <ul className="bm-item nav__block">
                        <li className="nav__list">
                            <button className="nav__link">link-1</button>
                        </li>

                        <li className="nav__list">
                            <button to="#" className="nav__link">link-2</button>
                        </li>
                        <li className="nav__list">
                            <button to="#" className="nav__link">link-3</button>
                        </li>
                        <li className="nav__list">
                            <button to="#" className="nav__link">link-4</button>
                        </li>
                        <li className="nav__list">
                            <button to="#" className="nav__link">link-5</button>
                        </li>
                    </ul>
                </Burger>
           )) }
                </>
                : null}
        </div>
    )

}


export default Menu;
