import React, { useCallback, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { useNavigate } from 'react-router-dom'
import { prodCategory } from '../store/actions/productCategory'

export default function SimilarProducts({pr}) {

  const navigate = useNavigate()
const dispatch = useDispatch()
const catProd = useSelector((state) => state.category.products);

console.log(catProd)

   useEffect(() => {
(async() => {
await dispatch(prodCategory({
  cat_id: pr.cat_id
}))
})()
   }, [])



    const singleItem = useCallback(async(id) => {
        navigate(`/home/${id}`)
        window.scrollTo({
          top: 0,
          behavior: 'smooth'
        })
      }, [])


  return (   

    <div className='cardStyleBottom'>
    {catProd.map((product) => 
    <div onClick={() => singleItem(product.id)} className='cardItemBottom'>
      <img className="card-img-top" src={product.image} alt="Card image cap" />
      <div className="card-body">
        <p className="card-text">{product.price + '$'}</p>
        <p className="card-text desc">{product.description}</p>
      </div>
    </div>
    )}
  </div>

  )
}
