import { createReducer } from "@reduxjs/toolkit"
import { basketList, basketCreate, basketAdd } from "../actions/basket"


const initialState = {
    basket: [],  //basket Data
    basketCreate: [],  //karzina stexcel hamar 
    basketAdd: [] ,  //basketAdd   
}


export default createReducer(initialState, {
    [basketList.pending]: (state, action) => {

    },
    [basketList.fulfilled]: (state, action) => {
        state.basket = action.payload.basket.map((item) => (
            item.product
        ))
    },
    [basketList.rejected]: (state, action) => {
        console.error(404)
    },



})
