import React, { useCallback, useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { useNavigate } from 'react-router-dom'
import { getProducts } from '../store/actions/products'
import RemoveShoppingCartIcon from '@mui/icons-material/RemoveShoppingCart';
import { delProduct } from '../store/actions/products';
import { toast } from 'react-toastify';
import EditIcon from '@mui/icons-material/Edit';
import Spinner from 'react-bootstrap/Spinner';
import Toast from './Toast';



export default function AdminWrapper() {

  const dispatch = useDispatch()
  const navigate = useNavigate()
  const products = useSelector((state) => state.products.productsData);
  const page = useSelector((state) = state.products.pages)
  const [load, setLoad] = useState(true)

  console.log(page)

  useEffect(() => {

    (async () => {
      setLoad(true)
      await dispatch(getProducts())
      setLoad(false)
    })()

  }, [getProducts])






  const singleItem = useCallback((el) => {

    navigate(`/home/${el}`)

  }, [])


  const handleDelete = useCallback(async(pr) => {

    const data = await dispatch(delProduct(pr))
if(data.payload){
  toast('Product Succesfully deleted')
}
await dispatch(getProducts())


  }, [])


  const handleEdit = useCallback((e) => {

    console.log(e)

  }, [])


  return (
    <div className='container'>
<Toast/>
{!load   ?    (
    <div className='cardStyle'>

        {products.map((item) => (
          <div key={item.id} className='cardItem'>
          <div onClick={() => singleItem(item.id)} className="card">
            <div>
            <figure className='figure'>
              <img className="card-img-top" src={item.image} alt="Card image cap" />
              <figcaption className="card-body">
              <h5 className="card-title">{item.title}</h5>
              <p className="card-text desc">{item.short_desc}</p>
              <p className="card-text">{item.price + '$'}</p>
              <button onClick={(e) => handleDelete(item.id, e.stopPropagation())}>
                Delete Product <RemoveShoppingCartIcon/>
              </button>
              <button onClick={(e) => handleEdit(item, e.stopPropagation())}>
                Edit Product <EditIcon/>
              </button>
              </figcaption>
              </figure>
</div>
          </div>
          </div>
        ))}

    </div>
)
       :  
       <div className='spL'>
       <Spinner className='spinnerLoad' animation="border" variant="dark" />
   </div>
       }

    </div> 

  )
}
