import React, { useCallback, useEffect, useState } from 'react';
import StarRatings from 'react-star-ratings';
import { useDispatch, useSelector } from 'react-redux'
import { getProducts } from '../store/actions/products';
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';
import FavoriteIcon from '@mui/icons-material/FavoriteBorder';
import 'react-toastify/dist/ReactToastify.css';
import Spinner from 'react-bootstrap/Spinner';
import { basketCreate, basketList } from '../store/actions/basket';
import { useNavigate, useParams } from 'react-router-dom';
import { likeList, likeProduct } from '../store/actions/likeProducts';
import { toast } from 'react-toastify';
import ArrowUpwardIcon from '@mui/icons-material/ArrowUpward';
import { createRating } from '../store/actions/rating';
import _ from 'lodash';
import Toast from './Toast';
import Pagination from './Pagination';
import LikeProduct from './LikeProduct';


export default function Wrapper() {

    const dispatch = useDispatch();
    const products = useSelector((state) => state.products.productsData);
    const navigate = useNavigate();
    const [loading, setLoading] = useState(true);
    const [show, setShow] = useState(false)
    const userData = JSON.parse(localStorage.getItem("userData"));
    const basket = useSelector((state) => state.basket.basket)
    const [id, setId] = useState(null)
    const likes = useSelector((state) => state.likes.likes)
    const { page } = useParams()



    useEffect(() => {
        (async () => {
            setLoading(true)
            await dispatch(getProducts(page))
            setLoading(false)
        })()

    }, [getProducts]);

    useEffect(() => {
        window.addEventListener('scroll', () => {
            (window.pageYOffset > 300)
                ? setShow(true)
                : setShow(false)
        });
    }, []);

    const goToTop = () => {
        window.scrollTo({
            top: 0,
            behavior: "smooth",
        });
    };



    const handleClick = useCallback(async (el) => {

        console.log(el)



        let error = false
        const prodId = basket.map((id) => (id.product_id))



        if (prodId.find((id) => id === el.id)) {
            error = true
            toast.error('Item already in cart!', {
                position: "top-center",
                autoClose: 1000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                theme: "dark",
            });
        }


        if (!userData) {
            toast.error('Please login to be add card!', {
                position: "top-center",
                autoClose: 1000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                theme: "dark",
            });
            return
        }

        else if (error) {
            return
        }


        const data = await dispatch(basketCreate({
            user_id: userData.id,
            product_id: el.id,
            quantity: el.quantity,
            price: el.price
        }))

        if (data.payload) {
            toast.success(`${el.title} added in your basket`, {
                position: "top-center",
                autoClose: 1000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                theme: "dark",
            });
        }

        await dispatch(basketList())
    }, [userData])

    /*
        const handleLike = useCallback(async (el) => {

            let error = false
            const likeId = likes.map((id) => (id.product_id))


            if (likeId.find((id) => id === el.id)) {
                error = true
                toast.error('Item already in your wishlist!', {
                    position: "top-center",
                    autoClose: 1000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                    theme: "dark",
                });
            }

            if (!userData) {
                toast.error('Please login to be add item in your wishlist!', {
                    position: "top-center",
                    autoClose: 1000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                    theme: "dark",
                });
                return
            }

            else if (error && userData) {
                return
            }


            const data = await dispatch(likeProduct({
                product_id: el.id,
                user_id: userData.id
            }))
            if (data.payload) {
                toast(`${el.title} added in your wishlist`)
            }
            await dispatch(likeList())
        }, [userData])
    */

        const handleId = useCallback((product) => {
         setId(id === product.id ? null : product)

        }, [id])



    const singleItem = useCallback((item) => {
        navigate(`/home/${item.id}`)
    }, [])



    return (
        <>
            <Toast />
            <div className='main'>
                <div className='container'>
                    {!loading ?
                        <div className='cardStyle'>
                            {products.map((item) => (
                                <div key={item.id} className='cardItem'>
                                    <div>
                                        <div onClick={() => singleItem(item)} className="card">
                                            <figure className='figure'>
                                                <img className="card-img-top" src={item.image} alt="Card image cap" />
                                                <figcaption className="card-body">
                                                    <h5 className="card-title">{item.title}</h5>
                                                    <p className="card-text desc">{item.short_desc}</p>
                                                    <p className="card-text">{item.price + '$'}</p>
                                                    <button onClick={(e) => handleClick(item, e.stopPropagation())}>
                                                        <AddShoppingCartIcon style={{ fontSize: 35 }} /> Add to card
                                                    </button>
                                                </figcaption>
                                            </figure>
                                        </div>
                                        <div className='btnChange'>
                                            <button onClick={() => handleId(item)} className='likeButton'>
                                                <FavoriteIcon  style={{ fontSize: 35, color: 'red' }} />
                                            </button>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                        :
                        <div className='spL'>
                            <Spinner className='spinnerLoad' animation="border" variant="dark" />
                        </div>

                    }
                    <div>
                        {show && (
                            <div className='top' onClick={goToTop}>
                                <ArrowUpwardIcon style={{ fontSize: 30, fill: 'white' }} />
                            </div>
                        )}
                    </div>
                    <Pagination />
                </div>
            </div>
        </>
    )
}
