import React, { useCallback, useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux'
import { getProducts } from '../store/actions/products';
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';
import FavoriteIcon from '@mui/icons-material/Favorite';
import 'react-toastify/dist/ReactToastify.css';
import Spinner from 'react-bootstrap/Spinner';
import { basketCreate, basketList } from '../store/actions/basket';
import CategoryData from './CategoryData';
import { Link, useNavigate } from 'react-router-dom';
import { likeList, likeProduct } from '../store/actions/likeProducts';
import { toast, ToastContainer } from 'react-toastify';
import ArrowUpwardIcon from '@mui/icons-material/ArrowUpward';
import _ from 'lodash';



export default function Wrapper() {

    const dispatch = useDispatch();
    const products = useSelector((state) => state.products.productsData);
    const navigate = useNavigate();
    const [loading, setLoading] = useState(true);
    const [show, setShow] = useState(false)
    const [open, setOpen] = useState(false);
    const token = JSON.parse(localStorage.getItem("userToken"));
    const likes = useSelector((state) => state.likes.likes);
    const userData = JSON.parse(localStorage.getItem("userData"));
    const [add, setAdd] = useState(false);


    useEffect(() => {

        (async () => {
            setLoading(true)
            await dispatch(getProducts())
            setLoading(false)
        })()

    }, [getProducts]);

    useEffect(() => {
        window.addEventListener('scroll', () => {
            (window.pageYOffset > 300)
                ? setShow(true)
                : setShow(false)
        });
    }, []);

    const goToTop = () => {
        window.scrollTo({
            top: 0,
            behavior: "smooth",
        });
    };

    const handleClick = useCallback(async (el) => {

        if (!userData) {
            toast.error('Please login to be add card!', {
                position: "top-center",
                autoClose: 1000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                theme: "dark",
            });
            return
        }
        const data = await dispatch(basketCreate({
            user_id: userData.id,
            product_id: el.id,
            quantity: el.quantity,
            price: el.price
        }))

        if (data.payload) {
           // toast(`${el.title} added in your basket`)
           toast.success(`${el.title} added in your basket`, {
            position: "top-center",
            autoClose: 1000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: "dark",
            });
        }


        if (!data.payload) {
            toast.error('Item already in cart!', {
                position: "top-center",
                autoClose: 1000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                theme: "dark",
            });
            return
        }
        console.log(data)
        await dispatch(basketList())
    }, [userData])


    const handleLike = useCallback(async (el) => {

        if (!userData) {
            toast.error('Please login to be add item in your wishlist!', {
                position: "top-center",
                autoClose: 1000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                theme: "dark",
            });
            return
        }
        const data = await dispatch(likeProduct({

            product_id: el.id,
            user_id: userData.id
        }))
        if (data.payload) {
            toast(`${el.title} added in your wishlist`)
        }

        if (!data.payload) {
            toast.error('Item already in cart!', {
                position: "top-center",
                autoClose: 1000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                theme: "dark",
            });
            return
        }

        await dispatch(likeList())

    }, [userData])


    const singleItem = useCallback((item) => {
        navigate(`/home/${item.id}`)
    }, [])


    return (
        <>
            <div>
                <CategoryData />
            </div>

            {!loading ?
                <div className='cardStyle'>

                    {products.map((item) => (
                        <div key={item.id} className='productsDiv'>
                            <div onClick={() => singleItem(item)} className="card ">
                                <img className="card-img-top" src={item.image} alt="Card image cap" />
                                <div className="card-body">
                                    <h5 className="card-title">{item.title}</h5>
                                    <p className="card-text desc">{item.description}</p>
                                    <p className="card-text">{item.price + '$'}</p>
                                    <p className="card-text">
                                        {item.quantity}
                                    </p>
                                </div>
                            </div>
                            <div className='btnChange'>
                                <button onClick={() => handleClick(item)}><AddShoppingCartIcon style={{ fontSize: 35 }} /></button>
                                <button onClick={() => handleLike(item)}><FavoriteIcon style={{ fontSize: 35 }} /></button>
                            </div>
                        </div>
                    ))}

                </div>
                :
                <div className='spL'>
                    <Spinner className='spinnerLoad' animation="border" variant="dark" />

                </div>

            }
            <div>
            {show && (
                    <div className='top' onClick={goToTop}>
                        <ArrowUpwardIcon style={{fontSize: 30, fill: 'white'}}/>
                    </div>
                )}
            </div>

        </>
    )
}
