import React, { useEffect, useState } from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import Account from './pages/Account';
import Card from './pages/Card';
import Home from './pages/Home';
import Products from './pages/Products';
import Registration from './pages/Registration';
import Category from './pages/Category';
import NotFound from './pages/NotFound';
import SingleProduct from './components/SingleProduct';
import Likeproducts from './pages/LikeProducts';
import Admin from './pages/Admin';
import RemoveAccount from './pages/RemoveAccount';
import { loadStripe } from '@stripe/stripe-js';
import { Elements } from '@stripe/react-stripe-js';
import CheckOutForm from './components/CheckOutForm';
import { payToken } from './store/actions/payment';
import { useDispatch } from 'react-redux';

const stripePromise = loadStripe('pk_test_51MuXL0CEQfpAPk12AZCsZ2eva7qDzIID1yez4yJkZGW746AF7p9NklCbBf8ItzOoyajzyw4Y7Z9yFSMb49ySuMn6009KMlMFGM');

function App(props) {

  const { clientSecret, setClientSecret } = useState()
  const token = localStorage.getItem("userToken")
  const dispatch = useDispatch()

  console.log(token)

  useEffect(() => {
    (async () => {
      if(token) {
      const data = await dispatch(payToken({
        "amount": 2000,
  "currency": "usd",
  "description": "Stripe Payment",
  "source":  token,
      }))

      }
    })()

  }, [])


  if (!clientSecret) {
    return null
  }


  const options = {
    clientSecret
  }



  return (
    <div>
      <BrowserRouter>
        <Routes>
          <Route path='/' element={<Navigate to='/home' />} />
          <Route path='/home' element={<Home />} />
          <Route path='/card' element={<Card />} />
          <Route path='/products' element={<Products />} />
          <Route path='/likeproducts' element={<Likeproducts />} />
          <Route path='/account' element={<Account />} />
          <Route path='/removeacc' element={<RemoveAccount />} />
          <Route path='/register' element={<Registration />} />
          <Route path='/category' element={<Category />} />
          <Route path='/category/:cat_id' element={<Category />} />
          <Route path='/home/:productId' element={<SingleProduct />} />
          <Route path='/admin' element={<Admin />} />
          <Route path='*' element={<NotFound />} />
        </Routes>
      </BrowserRouter>

      <div>
        <Elements stripe={stripePromise} options={options}>
          <CheckOutForm />
        </Elements>
      </div>
    </div>
  );
}

export default App;
