import { createAsyncThunk } from "@reduxjs/toolkit";
import Api from "../../Api";



export const getProducts = createAsyncThunk('action/getProducts', async() => {
 const {data} =  await Api.getData()
 return data  
})


export const searchProducts = createAsyncThunk('action/searchProducts', async(payload) => {
   console.log(payload)
   const {data} = await Api.searchProducts(payload)
   return data
})





export const basketList = createAsyncThunk('action/basketList', async() => {
 const {data} = await Api.basketList()  
 return data
})
