import axios from "axios";


const token = localStorage.getItem("userToken")

const api = axios.create({
  baseURL: 'http://localhost:3003'
})

console.log(token)





api.interceptors.request.use((config) => {
  config.headers.push(token)
  return config;
},

  (error) => {

    return Promise.reject(error);

  });






class Api {

  static getData() {
    return api.get('/products/')
  }

  static singleProduct(id) {
    return api.get(`/products/${id}`)
  }

  static searchProducts(search) {
    return api.get('/products/', {
      params: {
        q: search
      }
    })
  }



  static basketList() {
    return api.get('/basket/list')
  }


  static basketAdd(id) {
    return api.put(`/basket/${id}`)
  }


  static basketCreate(data) {
    return api.post(`/basket/create`, data)
  }



  static registration(data) {
    return api.post('/users/', data)
  }


  static login(data) {
    return api.post('/users/login', data)
  }


  static categoryList() {
    return api.get('/categories/')
  }


  static productCategory(id) {
    return api.get(`/products/?cat_id=${id}`)
  }




}








export default Api;
