import React, { useCallback, useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getProducts } from '../store/actions/products';
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';
import FavoriteIcon from '@mui/icons-material/Favorite';
import 'react-toastify/dist/ReactToastify.css';
import Spinner from 'react-bootstrap/Spinner';
import { basketCreate, basketList } from '../store/actions/basket';
import CategoryData from './CategoryData';
import { Link, useNavigate } from 'react-router-dom';
import { likeList, likeProduct } from '../store/actions/likeProducts';
import { toast, ToastContainer } from 'react-toastify';
import Menu from './Menu';



export default function Wrapper() {

    const dispatch = useDispatch()
    const products = useSelector((state) => state.products.productsData)
    const navigate = useNavigate()
    const [loading, setLoading] = useState(true)
    const [open, setOpen] = useState(false)
    const token = JSON.parse(localStorage.getItem("userToken"))
    const likes = useSelector((state) => state.likes.likes)
    const userData = JSON.parse(localStorage.getItem("userData"))



    useEffect(() => {

        (async () => {
            setLoading(true)
            await dispatch(getProducts())
            setLoading(false)
        })()

    }, [getProducts])




    const handleClick = useCallback(async (el) => {

    const id = userData.id
         const data = await dispatch(basketCreate({
            id: el.id,
            user_id: id,
            product_id: el.id,
            quantity: el.quantity,
            price: el.price
        }))
        if(data.payload){           
                toast(<Link to = '/card'>Product Added to card</Link>)
        }
        else
await dispatch(basketList())
    }, [])


const handleLike = useCallback(async(el) => {

    const userId = userData.id
const data = await dispatch(likeProduct({    
product_id: el.id,
user_id: userId
}))
await dispatch(likeList())
console.log(data)
}, [])


    const singleItem = useCallback((item) => {
        navigate(`/home/${item.id}`)
    }, [])


    return (
        <>
        <div>
            <CategoryData/>
        </div>
        <ToastContainer/>
            {!loading ?
                <div className='cardStyle'>

                    {products.map((item) => (
                        <div key={item.id} className='productsDiv'>
                            <div onClick={() => singleItem(item)} className="card ">
                                <img className="card-img-top" src={item.image} alt="Card image cap" />
                                <div className="card-body">
                                    <h5 className="card-title">{item.title}</h5>
                                    <p className="card-text desc">{item.description}</p>
                                    <p className="card-text">{item.price + '$'}</p>
                                    <p className="card-text">
                                        {item.quantity}
                                    </p>
                                </div>
                            </div>
                            <button onClick={() => handleClick(item)}><AddShoppingCartIcon style={{ fontSize: 35 }} /></button>
                            <button onClick={() => handleLike(item)}><FavoriteIcon style={{ fontSize: 35 }} /></button>
                        </div>
                    ))}

                </div>
                :
                <div className='spL'>
                    <Spinner className='spinnerLoad' animation="border" variant="dark" />

                </div>
            }
            {/* <Menu/> */}

        </>
    )
}
