import React, { useCallback, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import Header from '../components/Header'
import { addProduct } from '../store/actions/products'


export default function Admin() {

  const [brand, setBrand] = useState('')
  const [desc, setDesc] = useState('')
  const [shortDesc, setShortDesc] = useState('')
  const [price, setPrice] = useState('')
  const [cat, setCat] = useState('')
  const [img, setImg] = useState('')
  const [imgs, setImgs] = useState([])
  const [qnt, setQnt] = useState('')
  const [open, setOpen] = useState(false)
  const [add, setAdd] = useState(false)
  const list = useSelector((state) => state.categ)
  const dispatch = useDispatch()
  const userData = JSON.parse(localStorage.getItem("userData"))





  const handleSubmit = useCallback((ev) => {
    ev.preventDefault()

  }, [])

  const handleClick = useCallback(async () => {

    if (userData.role === 2) {
      const data = await dispatch(addProduct({
        title: brand,
        image: img,
        images: imgs,
        description: desc,
        price: price,
        quantity: qnt,
        short_desc: shortDesc,
        cat_id: cat
      }))
      console.log(data)
    }







  }, [qnt, imgs, img, desc, brand, shortDesc, price, cat])


  return (

    <div className='productMenu'>
      <button onClick={() => setOpen(!open)}>Add Product</button>
      {open ? (
        <form onSubmit={(ev) => handleSubmit(ev)}>
          <input value={brand} onChange={(ev) => setBrand(ev.target.value)} type='text' placeholder='Brand' />
          <input value={price} onChange={(ev) => setPrice(ev.target.value)} type='number' placeholder='Price' />
          <input value={qnt} onChange={(ev) => setQnt(ev.target.value)} type='number' placeholder='Quantity' />
          <input value={desc} onChange={(ev) => setDesc(ev.target.value)} type='text' placeholder='Product Description' />
          <input value={shortDesc} onChange={(ev) => setShortDesc(ev.target.value)} type='text' placeholder='Short Description' />
          <input value={img} onChange={(ev) => setImg(ev.target.value)} type='url' placeholder='Single Image URL' />
          <input value={imgs} onChange={(ev) => setImgs(ev.target.value)} type='url' placeholder='Images URL' />
          <input value={cat} onChange={(ev) => setCat(ev.target.value)} type='number' placeholder='Category Id' />
          <button onClick={() => handleClick()} type='submit'>ADD</button>
        </form>
      ) : null}
      <div>
        <button onClick={() => setAdd(!add)}>Add Category</button>
        {add ?
          (

<form>
<input type = 'text' placeholder='Your Category Title' />
<button>{}</button>
<button type = 'submit'>Add Category</button>

</form>


          )
            :
            null
}

      </div>
    </div>
  )
}
