import React, { useCallback, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Header from '../components/Header';
import Footer from '../components/Footer';
import { getProducts } from '../store/actions/products';
import { useNavigate, useParams } from 'react-router-dom';
import { prodCategory } from '../store/actions/productCategory';
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';
import FavoriteIcon from '@mui/icons-material/Favorite';
import { toast } from 'react-toastify';
import { basketCreate, basketList } from '../store/actions/basket';
import Toast from '../components/Toast';



export default function Category() {

  const dispatch = useDispatch();
  const products = useSelector((state) => state.category.products);
  const user = JSON.parse(localStorage.getItem("userData"))
  const { cat_id } = useParams();
  const navigate = useNavigate();
  const basket = useSelector((state) => state.basket.basket)
  const likes = useSelector((state) => state.likes.likes)





  useEffect(() => {

    (async () => {
      await dispatch(prodCategory(cat_id))
    })()


  }, []);


  const singleItem = useCallback((item) => {

    navigate(`/home/${item.id}`)

  }, [])


  const handleClick = useCallback(async (el) => {

    let error = false
    const prodId = basket.map((id) => (id.product_id))


    if (prodId.find((id) => id === el.id)) {
        error = true
        toast.error('Item already in cart!', {
            position: "top-center",
            autoClose: 1000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: "dark",
        });
    }


    if (!user) {
        toast.error('Please login to be add card!', {
            position: "top-center",
            autoClose: 1000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: "dark",
        });
        return
    }

else if(error){
  return
}


    const data = await dispatch(basketCreate({
        user_id: user.id,
        product_id: el.id,
        quantity: el.quantity,
        price: el.price
    }))

    if (data.payload) {
        toast.success(`${el.title} added in your basket`, {
            position: "top-center",
            autoClose: 1000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: "dark",
        });
    }

    await dispatch(basketList())
}, [user])


  const handleLike = useCallback((el) => {

console.log(el)

  }, [])



  return (
    <div>
      <Header />
      <Toast/>
      <div className='cardStyle'>
        {products.map((product) => (
          <div key={product.id} className='cardItem'>
            <div onClick={() => singleItem(product)} className="card ">
              <img className="card-img-top" src={product.image} alt="Card image cap" />
              <div className="card-body">
                <h5 className="card-title">{product.title}</h5>
                <p className="card-text desc">{product.description}</p>
                <p className="card-text">{product.price + '$'}</p>
              </div>
            </div>
            <div className='btnChange'>
              <button onClick={() => handleClick(product)}><AddShoppingCartIcon style={{ fontSize: 35 }} /></button>
              <button onClick={() => handleLike(product)}><FavoriteIcon style={{ fontSize: 35 }} /></button>
            </div>
          </div>
        ))}
      </div>
      <Footer />
    </div>
  )
}
