import React, { useCallback, useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { basketCreate, basketId, basketList, basketRemove } from '../store/actions/basket'
import RemoveShoppingCartIcon from '@mui/icons-material/RemoveShoppingCart';
import Toast from '../components/Toast';
import { toast } from 'react-toastify';
import PaidIcon from '@mui/icons-material/Paid';
import { useNavigate } from 'react-router-dom';

export default function CardCount({ b }) {

  const dispatch = useDispatch()
  const [count, setCount] = useState(1);
  const navigate = useNavigate()



  useEffect(() => {
    (async () => {
      await dispatch(basketList())
    })()

  }, [basketList])

  const handleDelete = useCallback(async (el) => {

    await dispatch(basketRemove(el))
    await dispatch(basketList())
  }, [])


  const handlePlus = useCallback((qnt) => {




  }, [count])



  const handleMinus = useCallback((qnt) => {

    if (count > 1) {
      setCount(count - 1)
    }
    else {
      toast('Quantity cannot be 0')
    }
  }, [count])


  const singleItem = useCallback((item) => {
    navigate(`/home/${item}`)

  }, [])


  return (
    <div>

      <div className='cardItem' key={b.product.id}>
        <div onClick={() => singleItem(b.product_id)} className="card">
          <img className="card-img-top" src={b.product.image} alt="Card image cap" />
          <div className="card-body">
            <h5 className="card-title">Brand: {b.product.title}</h5>
            <p className="card-text desc">Description: {b.product.description}</p>
            <p className="card-text">Price: {b.product.price + '$'}</p>
            <button className='' onClick={(e) => {
              e.stopPropagation()
              setCount(count - 1)
              }}>-</button>
            <p>{count}</p>
            <button onClick={(e) => {
              e.stopPropagation()
              setCount(count + 1)
            }}>+</button>

          </div>
        </div>
        <div className='btnChange'>
          <button onClick={() => handleDelete(b.id)}><RemoveShoppingCartIcon /></button>
        </div>
      </div>
    </div>

  )
}
