import React, { useCallback } from 'react'
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';
import RemoveShoppingCartIcon from '@mui/icons-material/RemoveShoppingCart';
import { delProduct } from '../store/actions/products';
import { prodCategory } from '../store/actions/productCategory';
import { useLocation, useNavigate, useParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { basketCreate, basketList } from '../store/actions/basket';
import { likeProduct, likeList } from '../store/actions/likeProducts';
import FavoriteSharpIcon from '@mui/icons-material/FavoriteSharp';
import FavoriteIcon from '@mui/icons-material/FavoriteBorder';
import { toast } from 'react-toastify';
import qs from 'query-string'


export default function CategoryProducts({ product }) {

  const user = JSON.parse(localStorage.getItem("userData"))
  const navigate = useNavigate();
  const dispatch = useDispatch()
  const basket = useSelector((state) => state.basket.basket)
  const likes = useSelector((state) => state.likes.likes)
  const { cat_id } = useParams();
  const location = useLocation()
  const query = qs.parse(location.search)


  const handleDelete = useCallback(async (pr) => {


    const data = await dispatch(delProduct(pr))
    if (data.payload) {
      toast('Product Succesfully deleted')
    }
    await dispatch(prodCategory({
      cat_id: cat_id,
      page: query.page
    }))
  }, [])

  const singleItem = useCallback((item) => {

    navigate(`/home/${item.id}`)

  }, [])


  const handleClick = useCallback(async (el) => {

    let error = false
    const prodId = basket.map((id) => (id.product_id))


    if (prodId.find((id) => id === el.id)) {
      error = true
      toast.error('Item already in cart!', {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
    }


    if (!user) {
      toast.error('Please login to be add card!', {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
      return
    }

    else if (error) {
      return
    }


    const data = await dispatch(basketCreate({
      user_id: user.id,
      product_id: el.id,
      quantity: el.quantity,
      price: el.price
    }))

    if (data.payload) {
      toast.success(`${el.title} added in your basket`, {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
    }

    await dispatch(basketList())
  }, [user])


  const handleLike = useCallback(async (el) => {


    let error = false
    const likeId = likes.map((id) => (id.product_id))

    if (likeId.find((id) => id === el.id)) {
      error = true
      toast.error('Item already in your wishlist!', {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
    }

    if (!user) {
      toast.error('Please login to be add item in your wishlist!', {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
      return
    }

    else if (error) {
      return
    }


    const data = await dispatch(likeProduct({
      product_id: el.id,
      user_id: user.id
    }))
    if (data.payload) {
      toast.success(`${el.title} added in your wishlist`)
    }

    await dispatch(likeList())

    // <AddShoppingCartIcon style={{ fontSize: 35 }} />
  }, [user])





  return (
    <div key={product.id} className='cardItem'>
      <div onClick={() => singleItem(product)} className="card ">
        <figure>
          <img className="card-img-top" src={product.image} alt="Card image cap" />
          <figcaption className="card-body">
            <h5 className="card-title">{product.title}</h5>
            <p className="card-text desc">{product.short_desc}</p>
            <p className="card-text">{product.price + '$'}</p>
            {user && user.role === 2 ?
              <button onClick={(e) => handleDelete(product.id, e.stopPropagation())}>
                Delete Product <RemoveShoppingCartIcon style={{ fontSize: 30 }} />
              </button>
              :
              <div>
                <div onClick={(e) => e.stopPropagation()}>
                  {basket.find((id) => id === item)}
                  <button onClick={() => handleClick(product)}>
                    <svg  xmlns="http://www.w3.org/2000/svg" width="35" height="35" fill="currentColor" class="bi bi-cart-plus-fill" viewBox="0 0 16 16">
                      <path d="M.5 1a.5.5 0 0 0 0 1h1.11l.401 1.607 1.498 7.985A.5.5 0 0 0 4 12h1a2 2 0 1 0 0 4 2 2 0 0 0 0-4h7a2 2 0 1 0 0 4 2 2 0 0 0 0-4h1a.5.5 0 0 0 .491-.408l1.5-8A.5.5 0 0 0 14.5 3H2.89l-.405-1.621A.5.5 0 0 0 2 1H.5zM6 14a1 1 0 1 1-2 0 1 1 0 0 1 2 0zm7 0a1 1 0 1 1-2 0 1 1 0 0 1 2 0zM9 5.5V7h1.5a.5.5 0 0 1 0 1H9v1.5a.5.5 0 0 1-1 0V8H6.5a.5.5 0 0 1 0-1H8V5.5a.5.5 0 0 1 1 0z" />
                    </svg>
                  </button>
                </div>
              </div>
            }
          </figcaption>
        </figure>
      </div>
      {user && user.role === 2 ? null :
        <div className='btnChange'>
          <button onClick={() => handleLike(product)} className='likeButton'>
            {likes.find((id) => id.product_id === product.id && user) ?
              <FavoriteSharpIcon style={{ fontSize: 35, color: 'red' }} />
              :
              <FavoriteIcon style={{ fontSize: 35, color: 'black' }} />}
          </button>
        </div>}
    </div>
  )
}
