import React, { useCallback, useEffect, useState } from 'react'
import { NavLink, useNavigate } from 'react-router-dom'
import MenuIcon from '@mui/icons-material/Menu';
import { categoryList, productCategory } from '../store/actions/productCategory';
import { useDispatch, useSelector } from 'react-redux';


export default function CategoryData() {

  const [open, setOpen] = useState(false)
const catList = useSelector((state) => state.category.catList)
const category = useSelector((state) => state.category.catProducts)

const dispatch = useDispatch()
const navigate = useNavigate()
console.log(category)


useEffect(() => {

dispatch(categoryList())

}, [])


const handleClick = useCallback((id) => {
dispatch(productCategory(id))
navigate(`/category?cat_id=${id}`)
}, [])


  return (
    <div className='navMenu'>
 <button onClick={() => setOpen(!open)}><MenuIcon style={{fontSize: 50}}/></button>

 {open ? 
<div className='navBtn'>
  {catList.map((item) => (
    <div className='menuButton' key={item.id}>
<button onClick={() => handleClick(item.id)}>{item.title}</button>

      </div>
  ))}


  </div>
:
null
 }



 </div>
  )
}
