import React, { useCallback, useEffect, useLayoutEffect, useRef, useState } from 'react';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import PhoneIcon from '@mui/icons-material/Phone';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import FavoriteIcon from '@mui/icons-material/Favorite';
import SearchIcon from '@mui/icons-material/Search';
import logo from '../assets/img/logo/Hebb.svg';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { searchProducts } from '../store/actions/products';
import useTypewriter from "react-typewriter-hook";
import LogoutIcon from '@mui/icons-material/Logout';
import PersonAddIcon from '@mui/icons-material/PersonAdd';
import { basketList } from '../store/actions/basket';
import { likeList } from '../store/actions/likeProducts';
import LoginIcon from '@mui/icons-material/Login';
import Menu from './Menu';
import AddIcon from '@mui/icons-material/Add';
import Modal from 'react-modal'


let index = 0;
const typing = [
    "Laptops...",
    "Electronics...",
    "Telephones...",
    "Clothes..."
];

export default function Header() {
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const [mod, setMod] = useState(false)
    const [value, setValue] = useState('');
    const [open, setOpen] = useState(false);
    const [placeholderValue, setPlaceholderValue] = useState("Search here...");
    const token = localStorage.getItem("userToken");
    const user = JSON.parse(localStorage.getItem("userData"))
    const intervalRef = useRef({});
    const ref = useRef();
    const hints = useTypewriter(placeholderValue);
    const basket = useSelector((state) => state.basket.basket);
    const likes = useSelector((state) => state.likes.likes);
    const stickyHeader = useRef();
    const [brand, setBrand] = useState('')
    const [desc, setDesc] = useState('')
    const [shortDesc, setShortDesc] = useState('')
    const [price, setPrice] = useState('')
    const [cat, setCat] = useState('')
    const [img, setImg] = useState('')
    const [imgs, setImgs] = useState('')
    const [qnt, setQnt] = useState('')
const list = useSelector((state) => state.category.catList)
const listChild = list.map((items) => (items.child.map((item) => (item)) ))


console.log(listChild)

    useEffect(() => {
        const handleOutsideClick = (event) => {
            if (!ref?.current?.contains(event.target)) {
                setOpen(false);
            }
        }
        window.addEventListener('click', handleOutsideClick);
    }, [ref]);


    useEffect(() => {     
        (async () => {
            if(token){
             await dispatch(basketList());
             await dispatch(likeList());
            }
        })()
    }, [basketList, likeList]);


    useEffect(
        () => {
            intervalRef.current = setInterval(() => {
                // index = index + 1 > 2 ? 0 : ++index + 1;
                index = index > 2 ? 0 : ++index;
                setPlaceholderValue(typing[index]);
            }, 6000);
            return function clear() {
                clearInterval(intervalRef.current);
            };
        }, [placeholderValue]);

    useLayoutEffect(() => {
        const mainHeader = document.getElementById('header');
        let fixedTop = stickyHeader.current?.offsetTop;
        stickyHeader.current.animation = 'bubble';
        const fixedHeader = () => {
            if (window?.pageYOffset > fixedTop) {
                mainHeader?.classList.add('fixedTop');
            } else {
                mainHeader?.classList.remove('fixedTop');
            }
        }
        window.addEventListener('scroll', fixedHeader);
    }, []);

    const toggle = useCallback((open) => {
        setOpen(!open);
    }, []);



    const handleSubmit = useCallback(async (e) => {
        e.preventDefault();
        if (value.length) {
            await dispatch(searchProducts(value));
            const product = '/products?q=';
            navigate(`${product}${value}`);
        } else {
            return null;
        }
    }, [value]);

    const handleLogout = useCallback(() => {
        localStorage.removeItem("userToken");
        localStorage.removeItem("userData");
        navigate('/')
    }, []);








    return (
        <>
            <header>
                <div id="topHeader">
                    <div className="container">
                        <div className="topHeaderLinks">
                            <ul className="headerLinks">
                                <li className='listItemStyle'>
                                    <Link to='tel:+37477053242' className='phoneNumber'>
                                        <PhoneIcon style={{ fontSize: 30, color: 'white' }} />
                                    </Link>
                                </li>

                                <li>
                                    <span className='spanUser'>{user ? `${user.fname} ${user.lname}` :  null}</span>
                                    <AccountCircleIcon
                                        ref={ref}
                                        onClick={() => toggle(open)}
                                        style={{ fontSize: 40, color: '#fff', cursor: 'pointer' }} />

                                    {open ?
                                        <div>
                                            {token ?
                                                <ul className="dropdownMenu">
                                                    <span className='triangle'></span>
                                                    <li className="menu-item">
                                                        <button onClick={() => handleLogout()} className='last'>LogOut <LogoutIcon style={{fontSize: 30}} /></button>
                                                    </li>
                                                    <span className='triangle'></span>
                                                    <li className="menu-item">
                                                        <button onClick={() => setMod(!mod)} className='last'>Add product <AddIcon style={{fontSize: 30}} /></button>
                                                    </li>
                                                </ul>
                                                :
                                                <ul className="dropdownMenu">
                                                    <span className='triangle'></span>
                                                    <li className="menu-item">
                                                        <Link to='/register'><PersonAddIcon style={{ fontSize: 30 }} /> Registration</Link>
                                                    </li>
                                                    <li className="menu-item">
                                                        <Link to='/account'><LoginIcon style={{ fontSize: 30 }} /> Login</Link>
                                                    </li>
                                                </ul>

                                            }
                                        </div>
                                        : null}
                                </li>
                            </ul>
                        </div>
                    </div>
                    {mod ? 
                    <Modal className='modall'  isOpen={true}>
                    <input type = 'text' placeholder='Brand'/>
                    <input type = 'url' placeholder='Single Image URL'/>
                    <input type = 'url' placeholder='Images URL'/>
                    <input type = 'text' placeholder='Product Description'/>
                    <input type = 'number' placeholder='Price'/>
                    <input type = 'number' placeholder='Quantity'/>
                    <input type = 'text' placeholder='Short Description'/>

                    <button onClick={() => setCategory(!categor)}>Category</button>
                    <button onClick={() => setMod(false)} type = 'submit'>Create Product</button>
                    </Modal>
                :
                null
                }
                </div>
                <div id="header" ref={stickyHeader}>

                    <div className="container">
                        <div className="headerLine">
                            <div className="menu">
                                <Menu />
                            </div>
                            <div className="headerLogo">
                                <Link to="/" className="logo">
                                    <img width={240} height={70} src={logo} alt="logo" />
                                </Link>
                            </div>
                            <div className="headerSearch">
                                <form onSubmit={(event) => handleSubmit(event)}>
                                    <label>
                                        <input
                                            value={value}
                                            onChange={(ev) => setValue(ev.target.value)}
                                            type='search'
                                            className="input"
                                            placeholder={hints}
                                        />
                                    </label>
                                    <button type='submit' className="searchBtn">
                                        <SearchIcon style={{ fontSize: 30 }} />
                                    </button>
                                </form>
                            </div>
                            <div className="clearfix">
                                <div className="headerCtn">
                                    <div className='likeCorner'>
                                        <a href='/likeproducts' className='btnIcon'>
                                            <FavoriteIcon style={{ fontSize: 40, color: 'red', }} />
                                            <span className="qty liked">{token ? likes.length : 0}</span>
                                        </a>
                                    </div>
                                    <div className='cartCorner'>
                                        <button onClick={() => navigate('/card')} className='btnIcon'>
                                            <ShoppingCartIcon style={{ fontSize: 40, color: 'lightcyan' }} />
                                            <span className="qty cart">{token ? basket.length : 0}</span>
                                        </button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </header>
        </>
    )
}
