import React, { useCallback, useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getProducts } from '../store/actions/action'
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';
import FavoriteIcon from '@mui/icons-material/Favorite';
import 'react-toastify/dist/ReactToastify.css';
import { ToastContainer, toast } from 'react-toastify';
import Spinner from 'react-bootstrap/Spinner';
import { basketAdd } from '../store/actions/action';
import { basketCreate } from '../store/actions/action';

export default function Wrapper() {

  const dispatch = useDispatch()
  const products = useSelector((state) => state.data)
  const [loading,setLoading] = useState(true)


  useEffect(() => {

   (async()=>{
setLoading(true)
     await dispatch(getProducts())
    setLoading(false)
  })()

  }, [getProducts])



const handleClick = useCallback(async(el) => {
await dispatch(basketAdd(el.id))

}, [])






  return (
    <>
    {!loading ?    
      <div className='cardStyle'>
        {products.map((item) => (
          <div key={item.id} className="card">
            <img className="card-img-top" src={item.image} alt="Card image cap" />
            <div className="card-body">
              <h5 className="card-title">Brand: {item.title}</h5>
              <p className="card-text desc">Description: {item.description}</p>
              <p className="card-text">Price: {item.price + '$'}</p>
              <p className="card-text">Quantity: {item.quantity}</p>

                <button><FavoriteIcon /></button>
                <button onClick={() => handleClick(item)}><AddShoppingCartIcon/> Add To Basket</button>
            </div>
          </div>
        ))}
        <ToastContainer/>
      </div>
      :
      <div className='spL'>
       <Spinner className='spinnerLoad' animation="border" variant="dark" />

       </div>
}

    </>
  )
}
