import React, { useCallback, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { likeProduct, likeList } from '../store/actions/likeProducts';
import { toast } from 'react-toastify';


export default function LikeCard({ item }) {

    const dispatch = useDispatch()
    const likes = useSelector((state) => state.likes.likes)
    const user = JSON.parse(localStorage.getItem("userData"))





const handleLike = useCallback(async () => {

    let error = false
    const likeId = likes.map((id) => (id.product_id))



    if (likeId.find((id) => id === item.id)){
        error = true
        toast.error('Item already in your wishlist!', {
            position: "top-center",
            autoClose: 1000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: "dark",
        });
    }

    if (!user) {
        toast.error('Please login to be add item in your wishlist!', {
            position: "top-center",
            autoClose: 1000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: "dark",
        });
        return
    }

    else if (error && user) {
        return
    }


    const data = await dispatch(likeProduct({
        product_id: item.id,
        user_id: user.id
    }))
    if (data.payload) {
        toast(`${item.title} added in your wishlist`)
    }
    await dispatch(likeList())
}, [user])


    return (
        <div key={item.id} className='cardItem'>
        <div>
            <div onClick={() => singleItem(item)} className="card">
                <figure className='figure'>
                    <img className="card-img-top" src={item.image} alt="Card image cap" />
                    <figcaption className="card-body">
                        <h5 className="card-title">{item.title}</h5>
                        <p className="card-text desc">{item.short_desc}</p>
                        <p className="card-text">{item.price + '$'}</p>
                        <button onClick={(e) => handleClick(item, e.stopPropagation())}>
                            <AddShoppingCartIcon style={{ fontSize: 35 }} /> Add to card
                        </button>
                    </figcaption>
                </figure>
            </div>

            <div className='btnChange'>
                <button onClick={() => handleId(item)} className='likeButton'>
                    <FavoriteIcon  style={{ fontSize: 35, color: 'red' }} />
                </button>
            </div>
        </div>
        </div>
    )
}
