            draggable: true,
            progress: undefined,
            theme: "dark",
        });
    }

    if (!user) {
        toast.error('Please login to be add item in your wishlist!', {
            position: "top-center",
            autoClose: 1000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: "dark",
        });
        return
    }

    else if (error && user) {
        return
    }


    const data = await dispatch(likeProduct({
        product_id: item.id,
        user_id: user.id
    }))
    if (data.payload) {
        toast(`${item.title} added in your wishlist`)
    }
    await dispatch(likeList())
